import React from 'react';
import { motion } from 'framer-motion';
import { FaBriefcase, FaFileAlt, FaGraduationCap, FaUserTie } from 'react-icons/fa';
import { useTheme } from '../contexts/ThemeContext';

const CVJobsPortal = () => {
  const { darkMode } = useTheme();

  const services = [
    {
      icon: <FaFileAlt />,
      title: 'Professional CV Writing',
      description: 'Our experts will craft a tailored CV that highlights your strengths and achievements to stand out to potential employers.',
      color: 'from-blue-500 to-blue-600'
    },
    {
      icon: <FaGraduationCap />,
      title: 'Academic CV Service',
      description: 'Specialized CVs for academic positions, highlighting research, publications, and teaching experience effectively.',
      color: 'from-purple-500 to-purple-600'
    },
    {
      icon: <FaUserTie />,
      title: 'Interview Preparation',
      description: 'Comprehensive coaching sessions to help you excel in job interviews, including practice questions and feedback.',
      color: 'from-green-500 to-green-600'
    },
    {
      icon: <FaBriefcase />,
      title: 'Job Search Assistance',
      description: 'Access to our extensive network of employers and job opportunities tailored to your skills and career goals.',
      color: 'from-amber-500 to-amber-600'
    }
  ];

  return (
    <div className="pt-20 lg:pt-24 min-h-screen bg-white dark:bg-gray-900">
      {/* Hero Section */}
      <section className="relative overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-r from-blue-500 to-indigo-600 opacity-10 dark:opacity-20"></div>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-16 md:py-24">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.6 }}
            className="text-center"
          >
            <h1 className="text-4xl md:text-5xl font-extrabold text-gray-900 dark:text-white mb-6">
              <span className="bg-clip-text text-transparent bg-gradient-to-r from-blue-600 to-indigo-600">
                CV & Jobs Portal
              </span>
            </h1>
            <p className="max-w-3xl mx-auto text-xl text-gray-600 dark:text-gray-300">
              Unlock your career potential with our professional CV writing services and exclusive job opportunities tailored to your skills and aspirations.
            </p>
          </motion.div>
        </div>
      </section>

      {/* Services Section */}
      <section className="py-20 bg-gray-50 dark:bg-gray-800">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-4">
              Our Career Services
            </h2>
            <p className="max-w-3xl mx-auto text-lg text-gray-600 dark:text-gray-300">
              We offer a range of professional services designed to help you advance your career
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
            {services.map((service, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className="bg-white dark:bg-gray-700 rounded-xl shadow-lg hover:shadow-xl transition-shadow duration-300 overflow-hidden"
              >
                <div className={`h-2 bg-gradient-to-r ${service.color}`}></div>
                <div className="p-6">
                  <div className="w-14 h-14 rounded-lg bg-blue-50 dark:bg-blue-900/30 flex items-center justify-center text-2xl mb-5 text-blue-600 dark:text-blue-300">
                    {service.icon}
                  </div>
                  <h3 className="text-xl font-semibold text-gray-900 dark:text-white mb-3">
                    {service.title}
                  </h3>
                  <p className="text-gray-600 dark:text-gray-300">
                    {service.description}
                  </p>
                </div>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Job Process Steps */}
      <section className="py-20">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-4">
              How It Works
            </h2>
            <p className="max-w-3xl mx-auto text-lg text-gray-600 dark:text-gray-300">
              Our streamlined process helps you get from CV to career in four simple steps
            </p>
          </motion.div>

          <div className="relative">
            {/* Connecting Line */}
            <div className="hidden md:block absolute left-1/2 top-0 bottom-0 w-1 bg-gray-200 dark:bg-gray-700 transform -translate-x-1/2 z-0"></div>
            
            <div className="space-y-12 md:space-y-0 relative z-10">
              {/* Step 1 */}
              <motion.div 
                initial={{ opacity: 0, x: -50 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6 }}
                className="md:grid md:grid-cols-2 md:gap-8 items-center"
              >
                <div className="md:text-right mb-8 md:mb-0">
                  <div className="bg-blue-50 dark:bg-blue-900/20 p-6 rounded-xl">
                    <span className="inline-block text-blue-600 dark:text-blue-300 font-bold mb-2">STEP 1</span>
                    <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-3">Initial Consultation</h3>
                    <p className="text-gray-600 dark:text-gray-300">
                      We start with a detailed assessment of your skills, experience, and career goals to understand your needs and aspirations.
                    </p>
                  </div>
                </div>
                <div className="flex justify-center md:justify-start">
                  <div className="w-12 h-12 rounded-full bg-blue-500 text-white flex items-center justify-center text-xl font-bold z-20">1</div>
                </div>
              </motion.div>
              
              {/* Step 2 */}
              <motion.div 
                initial={{ opacity: 0, x: 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6 }}
                className="md:grid md:grid-cols-2 md:gap-8 items-center"
              >
                <div className="flex justify-center md:justify-end order-1 md:order-none">
                  <div className="w-12 h-12 rounded-full bg-purple-500 text-white flex items-center justify-center text-xl font-bold z-20">2</div>
                </div>
                <div className="mb-8 md:mb-0">
                  <div className="bg-purple-50 dark:bg-purple-900/20 p-6 rounded-xl">
                    <span className="inline-block text-purple-600 dark:text-purple-300 font-bold mb-2">STEP 2</span>
                    <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-3">CV Development</h3>
                    <p className="text-gray-600 dark:text-gray-300">
                      Our expert writers create a tailored CV that highlights your strengths and achievements, formatted to industry standards.
                    </p>
                  </div>
                </div>
              </motion.div>
              
              {/* Step 3 */}
              <motion.div 
                initial={{ opacity: 0, x: -50 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6 }}
                className="md:grid md:grid-cols-2 md:gap-8 items-center"
              >
                <div className="md:text-right mb-8 md:mb-0">
                  <div className="bg-green-50 dark:bg-green-900/20 p-6 rounded-xl">
                    <span className="inline-block text-green-600 dark:text-green-300 font-bold mb-2">STEP 3</span>
                    <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-3">Job Matching</h3>
                    <p className="text-gray-600 dark:text-gray-300">
                      We match your profile with suitable job opportunities from our extensive network of employers across various industries.
                    </p>
                  </div>
                </div>
                <div className="flex justify-center md:justify-start">
                  <div className="w-12 h-12 rounded-full bg-green-500 text-white flex items-center justify-center text-xl font-bold z-20">3</div>
                </div>
              </motion.div>
              
              {/* Step 4 */}
              <motion.div 
                initial={{ opacity: 0, x: 50 }}
                whileInView={{ opacity: 1, x: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6 }}
                className="md:grid md:grid-cols-2 md:gap-8 items-center"
              >
                <div className="flex justify-center md:justify-end order-1 md:order-none">
                  <div className="w-12 h-12 rounded-full bg-amber-500 text-white flex items-center justify-center text-xl font-bold z-20">4</div>
                </div>
                <div>
                  <div className="bg-amber-50 dark:bg-amber-900/20 p-6 rounded-xl">
                    <span className="inline-block text-amber-600 dark:text-amber-300 font-bold mb-2">STEP 4</span>
                    <h3 className="text-2xl font-bold text-gray-900 dark:text-white mb-3">Interview Success</h3>
                    <p className="text-gray-600 dark:text-gray-300">
                      We provide comprehensive interview preparation, including mock interviews, feedback, and coaching to help you succeed.
                    </p>
                  </div>
                </div>
              </motion.div>
            </div>
          </div>
        </div>
      </section>

      {/* Testimonials */}
      <section className="py-20 bg-gray-50 dark:bg-gray-800">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <motion.div 
            initial={{ opacity: 0, y: 20 }}
            whileInView={{ opacity: 1, y: 0 }}
            viewport={{ once: true }}
            transition={{ duration: 0.6 }}
            className="text-center mb-16"
          >
            <h2 className="text-3xl font-bold text-gray-900 dark:text-white mb-4">
              Success Stories
            </h2>
            <p className="max-w-3xl mx-auto text-lg text-gray-600 dark:text-gray-300">
              Hear from our clients who successfully advanced their careers with our help
            </p>
          </motion.div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {[
              {
                name: "Sarah Johnson",
                role: "Software Developer",
                image: "https://randomuser.me/api/portraits/women/45.jpg",
                quote: "The CV writing service completely transformed my job prospects. Within two weeks of using my new CV, I received three interview calls and landed my dream job!"
              },
              {
                name: "Michael Chen",
                role: "Marketing Manager",
                image: "https://randomuser.me/api/portraits/men/52.jpg",
                quote: "The interview coaching was invaluable. They predicted almost all the questions I was asked and prepared me perfectly. I felt confident and secured the position."
              },
              {
                name: "Aisha Patel",
                role: "Research Scientist",
                image: "https://randomuser.me/api/portraits/women/63.jpg",
                quote: "As an academic, I struggled to present my research experience effectively. Their specialized academic CV service helped me secure a postdoctoral position at a top university."
              }
            ].map((testimonial, index) => (
              <motion.div
                key={index}
                initial={{ opacity: 0, y: 20 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                className="bg-white dark:bg-gray-700 p-6 rounded-xl shadow-lg"
              >
                <div className="flex items-center mb-4">
                  <img 
                    src={testimonial.image} 
                    alt={testimonial.name} 
                    className="w-14 h-14 rounded-full object-cover mr-4"
                  />
                  <div>
                    <h3 className="font-semibold text-gray-900 dark:text-white">{testimonial.name}</h3>
                    <p className="text-sm text-gray-500 dark:text-gray-400">{testimonial.role}</p>
                  </div>
                </div>
                <p className="text-gray-600 dark:text-gray-300 italic">"{testimonial.quote}"</p>
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* Call to Action */}
      <section className="bg-gradient-to-r from-blue-500 to-indigo-600 py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 text-center">
          <motion.div
            initial={{ opacity: 0, scale: 0.95 }}
            whileInView={{ opacity: 1, scale: 1 }}
            viewport={{ once: true }}
            transition={{ duration: 0.5 }}
          >
            <h2 className="text-3xl font-bold text-white mb-6">Ready to Advance Your Career?</h2>
            <p className="text-xl text-blue-100 mb-8 max-w-3xl mx-auto">
              Let our experts help you create the perfect CV and find your ideal job opportunity.
            </p>
            <div className="flex flex-col sm:flex-row gap-4 justify-center">
              <a 
                href="/contact" 
                className="inline-block bg-white text-blue-600 font-semibold py-3 px-8 rounded-xl shadow-md hover:shadow-lg transition-all duration-300 transform hover:-translate-y-1"
              >
                Get Started
              </a>
              <a 
                href="#" 
                className="inline-block bg-transparent text-white border-2 border-white font-semibold py-3 px-8 rounded-xl hover:bg-white hover:bg-opacity-10 transition-all duration-300"
              >
                View Sample CVs
              </a>
            </div>
          </motion.div>
        </div>
      </section>
    </div>
  );
};

export default CVJobsPortal;
