import React, { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import { AboutService } from './AdminDataServices';
import LoadingSpinner from '../../components/shared/LoadingSpinner';
import ErrorMessage from '../../components/shared/ErrorMessage';

const AboutSectionManagement = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description1: '',
    description2: '',
    image: '',
    yearsExperience: 0,
    learnMoreButtonText: '',
    learnMoreButtonLink: '',
    contactButtonText: '',
    contactButtonLink: ''
  });

  // Load about section data on component mount
  useEffect(() => {
    const loadAboutData = async () => {
      try {
        setLoading(true);
        const data = await AboutService.getAbout();
        setFormData({
          title: data.title || '',
          description1: data.description1 || '',
          description2: data.description2 || '',
          image: data.image || '',
          yearsExperience: data.yearsExperience || 0,
          learnMoreButtonText: data.learnMoreButtonText || '',
          learnMoreButtonLink: data.learnMoreButtonLink || '',
          contactButtonText: data.contactButtonText || '',
          contactButtonLink: data.contactButtonLink || ''
        });
        setLoading(false);
      } catch (err) {
        setError(err.message || 'Failed to load about section data');
        setLoading(false);
      }
    };

    loadAboutData();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'number' ? parseInt(value) : value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      await AboutService.updateAbout(formData);
      setLoading(false);
      toast.success('About section updated successfully');
    } catch (error) {
      toast.error(error.message || 'Something went wrong');
      setLoading(false);
    }
  };

  if (loading && !formData.title) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage message={error} />;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">About Section Management</h1>

      {/* About Section Form */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h2 className="text-xl font-semibold mb-4">Edit About Section</h2>
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="mb-4 md:col-span-2">
              <label className="block text-gray-700 mb-2" htmlFor="title">
                Title
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-4 md:col-span-2">
              <label className="block text-gray-700 mb-2" htmlFor="description1">
                Description (First Paragraph)
              </label>
              <textarea
                id="description1"
                name="description1"
                value={formData.description1}
                onChange={handleChange}
                rows="4"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              ></textarea>
            </div>

            <div className="mb-4 md:col-span-2">
              <label className="block text-gray-700 mb-2" htmlFor="description2">
                Description (Second Paragraph)
              </label>
              <textarea
                id="description2"
                name="description2"
                value={formData.description2}
                onChange={handleChange}
                rows="4"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              ></textarea>
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="image">
                Image URL
              </label>
              <input
                type="text"
                id="image"
                name="image"
                value={formData.image}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="yearsExperience">
                Years of Experience
              </label>
              <input
                type="number"
                id="yearsExperience"
                name="yearsExperience"
                value={formData.yearsExperience}
                onChange={handleChange}
                min="1"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="learnMoreButtonText">
                Learn More Button Text
              </label>
              <input
                type="text"
                id="learnMoreButtonText"
                name="learnMoreButtonText"
                value={formData.learnMoreButtonText}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="learnMoreButtonLink">
                Learn More Button Link
              </label>
              <input
                type="text"
                id="learnMoreButtonLink"
                name="learnMoreButtonLink"
                value={formData.learnMoreButtonLink}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="contactButtonText">
                Contact Button Text
              </label>
              <input
                type="text"
                id="contactButtonText"
                name="contactButtonText"
                value={formData.contactButtonText}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>

            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="contactButtonLink">
                Contact Button Link
              </label>
              <input
                type="text"
                id="contactButtonLink"
                name="contactButtonLink"
                value={formData.contactButtonLink}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
          </div>

          <div className="mt-6">
            <button
              type="submit"
              className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600 focus:outline-none focus:ring-2 focus:ring-blue-500"
              disabled={loading}
            >
              {loading ? 'Saving...' : 'Save Changes'}
            </button>
          </div>
        </form>
      </div>

      {/* Preview */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">Preview</h2>
        <div className="border border-gray-200 rounded-lg p-4">
          <h3 className="text-lg font-semibold mb-2">{formData.title}</h3>
          <p className="text-gray-600 mb-2">{formData.description1}</p>
          <p className="text-gray-600 mb-2">{formData.description2}</p>
          <div className="flex items-center mt-4">
            <span className="text-gray-700 mr-2">Years of Experience:</span>
            <span className="font-semibold">{formData.yearsExperience}+</span>
          </div>
          <div className="mt-2">
            <img 
              src={formData.image} 
              alt="About section preview" 
              className="w-full max-h-40 object-cover rounded-md"
              onError={(e) => {
                e.target.onerror = null;
                e.target.src = 'https://via.placeholder.com/400x200?text=Image+Preview';
              }}
            />
          </div>
          <div className="flex flex-wrap gap-2 mt-4">
            <div className="px-4 py-2 bg-navy text-white rounded-md">
              {formData.learnMoreButtonText}
            </div>
            <div className="px-4 py-2 border border-navy text-navy rounded-md">
              {formData.contactButtonText}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AboutSectionManagement;
