import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import useTheme from '../hooks/useTheme';

// Default team members data if nothing is in localStorage
const defaultTeamMembers = [
  {
    _id: 'team-1',
    name: "Dr. Sarah Williams",
    role: "Founder & CEO",
    image: "https://images.unsplash.com/photo-1573496359142-b8d87734a5a2?ixlib=rb-4.0.3",
    bio: "With over 15 years of experience in education consulting, Sarah founded GradWay to help students achieve their academic dreams.",
    order: 1
  },
  {
    _id: 'team-2',
    name: "Michael Chen",
    role: "Admissions Specialist",
    image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3",
    bio: "Michael specializes in university admissions and has helped hundreds of students secure spots in top universities worldwide.",
    order: 2
  },
  {
    _id: 'team-3',
    name: "Emma Wilson",
    role: "Visa Consultant",
    image: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3",
    bio: "Emma's expertise in visa applications and immigration processes ensures smooth transitions for our students.",
    order: 3
  }
];

// Default about page data
const defaultAboutData = {
  hero: {
    title: "About GradWay",
    description: "We are dedicated to helping students achieve their academic dreams through personalized guidance and expert support."
  },
  mission: {
    title: "Our Mission",
    description1: "To provide comprehensive educational consulting services that empower students to achieve their academic goals and unlock their full potential.",
    description2: "We believe in personalized guidance, transparent processes, and unwavering support throughout every student's educational journey."
  },
  vision: {
    title: "Our Vision",
    description1: "To be the leading educational consultancy, recognized for our commitment to excellence and student success.",
    description2: "We envision a world where every student has access to quality education and the support they need to succeed."
  },
  team: {
    title: "Meet Our Team",
    description: "Our team of experienced professionals is dedicated to helping you achieve your academic goals."
  },
  cta: {
    title: "Ready to Start Your Journey?",
    description: "Contact us today to learn how we can help you achieve your academic goals.",
    buttonText: "Get in Touch",
    buttonLink: "/contact"
  }
};

const AboutPage = () => {
  const theme = useTheme();
  const [aboutData, setAboutData] = useState(defaultAboutData);
  const [teamMembers, setTeamMembers] = useState(defaultTeamMembers);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Animation variants
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: { 
      opacity: 1,
      transition: { 
        staggerChildren: 0.2,
        delayChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0, transition: { duration: 0.6 } }
  };

  // Fetch about page data from localStorage
  useEffect(() => {
    const fetchAboutPageData = () => {
      try {
        setLoading(true);
        // Get about page data from localStorage
        const storedAboutPage = JSON.parse(localStorage.getItem('aboutPageDatabase'));

        if (storedAboutPage) {
          setAboutData({
            hero: storedAboutPage.hero || defaultAboutData.hero,
            mission: storedAboutPage.mission || defaultAboutData.mission,
            vision: storedAboutPage.vision || defaultAboutData.vision,
            team: storedAboutPage.team || defaultAboutData.team,
            cta: storedAboutPage.cta || defaultAboutData.cta
          });

          // Get team members
          if (storedAboutPage.teamMembers && storedAboutPage.teamMembers.length > 0) {
            // Sort by order if available
            const sortedTeamMembers = [...storedAboutPage.teamMembers].sort((a, b) => (a.order || 0) - (b.order || 0));
            setTeamMembers(sortedTeamMembers);
          } else {
            setTeamMembers(defaultTeamMembers);
          }
        } else {
          // Use default data if nothing is in localStorage
          setAboutData(defaultAboutData);
          setTeamMembers(defaultTeamMembers);
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching about page data:', err);
        setError('Failed to load about page data');
        setAboutData(defaultAboutData);
        setTeamMembers(defaultTeamMembers);
        setLoading(false);
      }
    };

    // Initial fetch
    fetchAboutPageData();

    // Set up storage event listener to detect changes from other tabs/windows
    const handleStorageChange = (e) => {
      if (e.key === 'aboutPageDatabase') {
        console.log('About page database updated, refreshing...');
        fetchAboutPageData();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Cleanup
    return () => {
      window.removeEventListener('storage', handleStorageChange);
    };
  }, []);

  if (loading) {
    return (
      <div className="pt-16 flex justify-center items-center min-h-screen">
        <div className="spinner"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="pt-16 flex justify-center items-center min-h-screen">
        <div className="text-center">
          <p className="text-red-500 text-xl">{error}</p>
          <p className="mt-4">Please try refreshing the page.</p>
        </div>
      </div>
    );
  }

  return (
    <motion.div 
      className="pt-16"
      initial="hidden"
      animate="visible"
      variants={containerVariants}
    >
      {/* Hero Section */}
      <section className="relative py-28 overflow-hidden">
        {/* Background Gradient & Decoration */}
        <div className="absolute inset-0 bg-gradient-to-br from-indigo-900/90 via-blue-800/80 to-purple-800/70"></div>

        {/* Decorative Elements */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute -right-24 top-1/4 w-64 h-64 rounded-full bg-indigo-400 opacity-20 blur-3xl"></div>
          <div className="absolute left-1/4 bottom-0 w-80 h-80 rounded-full bg-blue-300 opacity-20 blur-3xl"></div>
          <div className="pattern-dots opacity-10 absolute inset-0"></div>
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <motion.div 
            className="max-w-3xl mx-auto text-center"
            variants={itemVariants}
          >
            <span className="inline-block px-4 py-1.5 bg-white/10 backdrop-blur-sm rounded-full text-white text-sm font-medium mb-6">
              Established Since 2020
            </span>

            <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-6 leading-tight">
              <span className="text-gradient-light">
                {aboutData.hero.title}
              </span>
            </h1>

            <p className="text-xl text-white/90 max-w-3xl mx-auto">
              {aboutData.hero.description}
            </p>
          </motion.div>
        </div>
      </section>

      {/* Mission & Vision */}
      <section className="py-24 relative overflow-hidden">
        {/* Background decoration */}
        <div className="absolute inset-0 opacity-5 pointer-events-none overflow-hidden">
          <div className="absolute -left-32 -top-32 w-96 h-96 rounded-full bg-indigo-300"></div>
          <div className="absolute right-0 top-1/3 w-64 h-64 rounded-full bg-blue-300"></div>
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-16">
            {/* Mission */}
            <motion.div 
              className="bg-white p-8 rounded-2xl shadow-xl border border-gray-100 hover:shadow-2xl hover:border-indigo-100 transition-all duration-500"
              variants={itemVariants}
            >
              <div className="w-16 h-16 rounded-xl bg-indigo-100 flex items-center justify-center mb-6">
                <span className="text-3xl">🚀</span>
              </div>
              <h2 className="text-3xl font-bold text-gradient mb-6">{aboutData.mission.title}</h2>
              <div className="h-1 w-20 bg-gradient-to-r from-indigo-600 to-blue-500 rounded-full mb-6"></div>
              <p className="text-gray-700 mb-6 text-lg leading-relaxed">
                {aboutData.mission.description1}
              </p>
              <p className="text-gray-700 text-lg leading-relaxed">
                {aboutData.mission.description2}
              </p>
            </motion.div>

            {/* Vision */}
            <motion.div 
              className="bg-white p-8 rounded-2xl shadow-xl border border-gray-100 hover:shadow-2xl hover:border-indigo-100 transition-all duration-500"
              variants={itemVariants}
            >
              <div className="w-16 h-16 rounded-xl bg-blue-100 flex items-center justify-center mb-6">
                <span className="text-3xl">🔭</span>
              </div>
              <h2 className="text-3xl font-bold text-gradient mb-6">{aboutData.vision.title}</h2>
              <div className="h-1 w-20 bg-gradient-to-r from-indigo-600 to-blue-500 rounded-full mb-6"></div>
              <p className="text-gray-700 mb-6 text-lg leading-relaxed">
                {aboutData.vision.description1}
              </p>
              <p className="text-gray-700 text-lg leading-relaxed">
                {aboutData.vision.description2}
              </p>
            </motion.div>
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section className="py-24 bg-gradient-to-b from-indigo-50 to-white relative overflow-hidden">
        <div className="absolute inset-0 pattern-dots opacity-5"></div>

        <div className="container mx-auto px-4 relative z-10">
          <motion.div 
            className="text-center mb-16"
            variants={itemVariants}
          >
            <span className="inline-block px-3 py-1.5 bg-indigo-100 text-indigo-700 rounded-full text-sm font-medium mb-4">
              Our Experts
            </span>
            <h2 className="text-3xl md:text-4xl font-bold text-gradient mb-4">
              {aboutData.team.title}
            </h2>
            <div className="w-20 h-1.5 bg-gradient-to-r from-indigo-600 to-blue-500 rounded-full mx-auto mb-6"></div>
            <p className="text-gray-600 max-w-2xl mx-auto text-lg">
              {aboutData.team.description}
            </p>
          </motion.div>

          {teamMembers.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-600">No team members available at the moment.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-3 gap-10">
              {teamMembers.map((member, index) => (
                <motion.div
                  key={member._id}
                  className="bg-white p-8 rounded-2xl shadow-xl hover:shadow-2xl border border-gray-100 hover:border-indigo-100 transition-all duration-300 transform hover:-translate-y-2"
                  variants={itemVariants}
                  custom={index}
                >
                  <div className="w-36 h-36 rounded-full overflow-hidden mx-auto mb-6 ring-4 ring-indigo-100">
                    <img
                      src={member.image}
                      alt={member.name}
                      className="w-full h-full object-cover"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = 'https://via.placeholder.com/128?text=No+Image';
                      }}
                    />
                  </div>
                  <h3 className="text-2xl font-bold text-indigo-700 text-center mb-2">
                    {member.name}
                  </h3>
                  <p className="text-blue-600 text-center mb-6 font-medium">{member.role}</p>
                  <p className="text-gray-600 text-center">{member.bio}</p>
                </motion.div>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-24 relative overflow-hidden">
        {/* Background */}
        <div className="absolute inset-0 bg-gradient-to-r from-indigo-600 to-blue-500"></div>

        {/* Decorative elements */}
        <div className="absolute inset-0 overflow-hidden pointer-events-none">
          <div className="absolute -right-40 -top-40 w-96 h-96 rounded-full bg-purple-400 opacity-20 blur-3xl"></div>
          <div className="absolute -left-20 top-1/3 w-72 h-72 rounded-full bg-blue-300 opacity-20 blur-3xl"></div>
        </div>

        <div className="container mx-auto px-4 relative z-10">
          <div className="max-w-4xl mx-auto bg-white/10 backdrop-blur-lg rounded-3xl p-12 border border-white/20 shadow-2xl">
            <motion.div 
              className="text-center"
              variants={itemVariants}
            >
              <h2 className="text-3xl md:text-4xl font-bold text-white mb-6">
                {aboutData.cta.title}
              </h2>
              <p className="text-xl text-white/90 mb-10 max-w-2xl mx-auto">
                {aboutData.cta.description}
              </p>
              <Link
                to={aboutData.cta.buttonLink}
                className="px-8 py-4 bg-white text-indigo-600 font-semibold rounded-full hover:bg-gray-100 transition-all duration-300 shadow-xl shadow-indigo-900/20 transform hover:-translate-y-1 text-center"
              >
                {aboutData.cta.buttonText}
              </Link>
            </motion.div>
          </div>
        </div>
      </section>
    </motion.div>
  );
};

export default AboutPage;
