import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import LoadingSpinner from '../../components/shared/LoadingSpinner';
import ErrorMessage from '../../components/shared/ErrorMessage';

const ContentManagement = () => {
  const [activeTab, setActiveTab] = useState('hero');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [content, setContent] = useState({
    hero: null,
    about: null,
    services: null,
    admissions: null,
    countries: null
  });

  const [formData, setFormData] = useState({
    title: '',
    description: '',
    features: [],
    steps: [],
    countries: []
  });

  useEffect(() => {
    fetchContent();
  }, [activeTab]);

  const fetchContent = async () => {
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/content/${activeTab}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (data.success) {
        setContent(prev => ({ ...prev, [activeTab]: data.content }));
        setFormData(data.content || {
          title: '',
          description: '',
          features: [],
          steps: [],
          countries: []
        });
      }
    } catch (err) {
      setError('Failed to fetch content');
      toast.error('Failed to fetch content');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      const token = localStorage.getItem('token');
      const response = await fetch(`http://localhost:5000/api/content/${activeTab}`, {
        method: content[activeTab] ? 'PUT' : 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      if (data.success) {
        toast.success('Content updated successfully');
        fetchContent();
      }
    } catch (err) {
      toast.error('Failed to update content');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleArrayInputChange = (index, field, value, arrayName) => {
    setFormData(prev => {
      const newArray = [...prev[arrayName]];
      if (typeof newArray[index] === 'string') {
        newArray[index] = value;
      } else {
        newArray[index] = { ...newArray[index], [field]: value };
      }
      return { ...prev, [arrayName]: newArray };
    });
  };

  const addArrayItem = (arrayName) => {
    setFormData(prev => ({
      ...prev,
      [arrayName]: [...prev[arrayName], arrayName === 'features' ? '' : { title: '', description: '' }]
    }));
  };

  const removeArrayItem = (index, arrayName) => {
    setFormData(prev => ({
      ...prev,
      [arrayName]: prev[arrayName].filter((_, i) => i !== index)
    }));
  };

  const tabs = [
    { id: 'hero', label: 'Hero Section' },
    { id: 'about', label: 'About Section' },
    { id: 'services', label: 'Services' },
    { id: 'admissions', label: 'Admissions' },
    { id: 'countries', label: 'Countries' }
  ];

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="max-w-4xl mx-auto">
      <h1 className="text-3xl font-bold mb-8">Content Management</h1>
      
      {/* Tabs */}
      <div className="flex space-x-4 mb-8">
        {tabs.map(tab => (
          <button
            key={tab.id}
            onClick={() => setActiveTab(tab.id)}
            className={`px-4 py-2 rounded-lg ${
              activeTab === tab.id
                ? 'bg-navy text-white'
                : 'bg-gray-100 text-gray-600 hover:bg-gray-200'
            }`}
          >
            {tab.label}
          </button>
        ))}
      </div>

      {/* Content Form */}
      <form onSubmit={handleSubmit} className="space-y-6">
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium text-gray-700">Title</label>
            <input
              type="text"
              name="title"
              value={formData.title || ''}
              onChange={handleInputChange}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-navy focus:ring-navy"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700">Description</label>
            <textarea
              name="description"
              value={formData.description || ''}
              onChange={handleInputChange}
              rows={4}
              className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-navy focus:ring-navy"
            />
          </div>

          {/* Features Section */}
          {(activeTab === 'about' || activeTab === 'services') && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Features</label>
              {formData.features?.map((feature, index) => (
                <div key={index} className="flex gap-2 mb-2">
                  <input
                    type="text"
                    value={feature}
                    onChange={(e) => handleArrayInputChange(index, null, e.target.value, 'features')}
                    className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-navy focus:ring-navy"
                  />
                  <button
                    type="button"
                    onClick={() => removeArrayItem(index, 'features')}
                    className="px-3 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
                  >
                    Remove
                  </button>
                </div>
              ))}
              <button
                type="button"
                onClick={() => addArrayItem('features')}
                className="mt-2 px-4 py-2 bg-navy text-white rounded-md hover:bg-navy-dark"
              >
                Add Feature
              </button>
            </div>
          )}

          {/* Steps Section */}
          {activeTab === 'admissions' && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Steps</label>
              {formData.steps?.map((step, index) => (
                <div key={index} className="border p-4 rounded-md mb-4">
                  <div className="flex gap-2 mb-2">
                    <input
                      type="text"
                      value={step.title}
                      onChange={(e) => handleArrayInputChange(index, 'title', e.target.value, 'steps')}
                      placeholder="Step Title"
                      className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-navy focus:ring-navy"
                    />
                    <button
                      type="button"
                      onClick={() => removeArrayItem(index, 'steps')}
                      className="px-3 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
                    >
                      Remove
                    </button>
                  </div>
                  <textarea
                    value={step.description}
                    onChange={(e) => handleArrayInputChange(index, 'description', e.target.value, 'steps')}
                    placeholder="Step Description"
                    rows={2}
                    className="w-full rounded-md border-gray-300 shadow-sm focus:border-navy focus:ring-navy"
                  />
                </div>
              ))}
              <button
                type="button"
                onClick={() => addArrayItem('steps')}
                className="mt-2 px-4 py-2 bg-navy text-white rounded-md hover:bg-navy-dark"
              >
                Add Step
              </button>
            </div>
          )}

          {/* Countries Section */}
          {activeTab === 'countries' && (
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Countries</label>
              {formData.countries?.map((country, index) => (
                <div key={index} className="border p-4 rounded-md mb-4">
                  <div className="flex gap-2 mb-2">
                    <input
                      type="text"
                      value={country.name}
                      onChange={(e) => handleArrayInputChange(index, 'name', e.target.value, 'countries')}
                      placeholder="Country Name"
                      className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-navy focus:ring-navy"
                    />
                    <input
                      type="text"
                      value={country.flag}
                      onChange={(e) => handleArrayInputChange(index, 'flag', e.target.value, 'countries')}
                      placeholder="Flag Emoji"
                      className="w-20 rounded-md border-gray-300 shadow-sm focus:border-navy focus:ring-navy"
                    />
                    <button
                      type="button"
                      onClick={() => removeArrayItem(index, 'countries')}
                      className="px-3 py-2 bg-red-500 text-white rounded-md hover:bg-red-600"
                    >
                      Remove
                    </button>
                  </div>
                  <textarea
                    value={country.description}
                    onChange={(e) => handleArrayInputChange(index, 'description', e.target.value, 'countries')}
                    placeholder="Country Description"
                    rows={2}
                    className="w-full rounded-md border-gray-300 shadow-sm focus:border-navy focus:ring-navy"
                  />
                </div>
              ))}
              <button
                type="button"
                onClick={() => addArrayItem('countries')}
                className="mt-2 px-4 py-2 bg-navy text-white rounded-md hover:bg-navy-dark"
              >
                Add Country
              </button>
            </div>
          )}
        </div>

        <div className="flex justify-end">
          <button
            type="submit"
            className="px-6 py-3 bg-navy text-white rounded-md hover:bg-navy-dark"
            disabled={loading}
          >
            {loading ? 'Saving...' : 'Save Changes'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default ContentManagement;
