import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate, Outlet, useLocation } from 'react-router-dom';
import { FaGraduationCap, FaHandshake, FaComments, FaEnvelope, FaSignOutAlt, FaHome, FaUsers, FaBlog, FaCog } from 'react-icons/fa';
import Courses from './Courses';
import Services from './Services';
import Testimonials from './Testimonials';
import Contacts from './Contacts';
import Overview from './Overview';
import Blog from './Blog';
import Team from './Team';
import Hero from './Hero';

const Dashboard = () => {
  const location = useLocation();
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [stats, setStats] = useState({
    courses: 0,
    services: 0,
    testimonials: 0,
    contacts: 0
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchDashboardStats();
  }, []);

  const fetchDashboardStats = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/admin/stats', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (data.success) {
        setStats(data.stats);
      }
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/admin/login');
  };

  const menuItems = [
    {
      title: 'Overview',
      path: '/admin',
      icon: <FaHome className="w-5 h-5" />,
    },
    {
      title: 'Content Management',
      path: '/admin/content',
      icon: <FaCog className="w-5 h-5" />,
    },
    {
      title: 'Courses',
      path: '/admin/courses',
      icon: <FaGraduationCap className="w-5 h-5" />,
    },
    {
      title: 'Testimonials',
      path: '/admin/testimonials',
      icon: <FaComments className="w-5 h-5" />,
    },
    {
      title: 'Contact Requests',
      path: '/admin/contacts',
      icon: <FaEnvelope className="w-5 h-5" />,
    },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={`${
          isSidebarOpen ? 'w-64' : 'w-20'
        } bg-navy text-white transition-all duration-300 ease-in-out`}
      >
        <div className="flex items-center justify-between p-4">
          {isSidebarOpen && <h1 className="text-xl font-bold">Admin Dashboard</h1>}
          <button
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
            className="p-2 rounded-lg hover:bg-blue-700"
          >
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M4 6h16M4 12h16M4 18h16"
              />
            </svg>
          </button>
        </div>

        <nav className="mt-8">
          {menuItems.map((item) => (
            <Link
              key={item.path}
              to={item.path}
              className={`flex items-center px-4 py-3 ${
                location.pathname === item.path
                  ? 'bg-blue-700'
                  : 'hover:bg-blue-700'
              }`}
            >
              {item.icon}
              {isSidebarOpen && (
                <span className="ml-3">{item.title}</span>
              )}
            </Link>
          ))}
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 overflow-auto">
        <div className="p-8">
          <Outlet />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
