import React, { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { fetchContentByPage, createContent, updateContent } from '../../features/content/contentSlice';
import { toast } from 'react-toastify';
import { Editor } from '@tinymce/tinymce-react';
import LoadingSpinner from '../../components/shared/LoadingSpinner';
import ErrorMessage from '../../components/shared/ErrorMessage';

const ContentManagement = () => {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { user } = useSelector((state) => state.auth);
  const { content, loading, error } = useSelector((state) => state.content);

  const [selectedPage, setSelectedPage] = useState('home');
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    content: {},
    images: [],
    meta: {
      title: '',
      description: '',
      keywords: []
    }
  });

  useEffect(() => {
    if (!user || !user.isAdmin) {
      navigate('/login');
    }
  }, [user, navigate]);

  useEffect(() => {
    if (selectedPage) {
      dispatch(fetchContentByPage(selectedPage));
    }
  }, [dispatch, selectedPage]);

  useEffect(() => {
    if (content) {
      setFormData({
        title: content.title || '',
        description: content.description || '',
        content: content.content || {},
        images: content.images || [],
        meta: content.meta || {
          title: '',
          description: '',
          keywords: []
        }
      });
    }
  }, [content]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (content) {
        await dispatch(updateContent({ id: content._id, contentData: formData })).unwrap();
        toast.success('Content updated successfully');
      } else {
        await dispatch(createContent({ ...formData, page: selectedPage })).unwrap();
        toast.success('Content created successfully');
      }
    } catch (error) {
      toast.error(error.message || 'Something went wrong');
    }
  };

  const handleEditorChange = (content, editor) => {
    setFormData(prev => ({
      ...prev,
      content: {
        ...prev.content,
        [editor.id]: content
      }
    }));
  };

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-8">Content Management</h1>

      <div className="mb-6">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Select Page
        </label>
        <select
          value={selectedPage}
          onChange={(e) => setSelectedPage(e.target.value)}
          className="w-full p-2 border rounded"
        >
          <option value="home">Home</option>
          <option value="about">About</option>
          <option value="services">Services</option>
          <option value="contact">Contact</option>
          <option value="blog">Blog</option>
        </select>
      </div>

      <form onSubmit={handleSubmit} className="space-y-6">
        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Title
          </label>
          <input
            type="text"
            value={formData.title}
            onChange={(e) => setFormData(prev => ({ ...prev, title: e.target.value }))}
            className="w-full p-2 border rounded"
            required
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Description
          </label>
          <textarea
            value={formData.description}
            onChange={(e) => setFormData(prev => ({ ...prev, description: e.target.value }))}
            className="w-full p-2 border rounded"
            rows="3"
            required
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Content
          </label>
          <Editor
            apiKey="your-tinymce-api-key"
            value={formData.content[selectedPage] || ''}
            onEditorChange={(content) => handleEditorChange(content, { id: selectedPage })}
            init={{
              height: 500,
              menubar: false,
              plugins: [
                'advlist autolink lists link image charmap print preview anchor',
                'searchreplace visualblocks code fullscreen',
                'insertdatetime media table paste code help wordcount'
              ],
              toolbar:
                'undo redo | formatselect | bold italic backcolor | \
                alignleft aligncenter alignright alignjustify | \
                bullist numlist outdent indent | removeformat | help'
            }}
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Meta Title
          </label>
          <input
            type="text"
            value={formData.meta.title}
            onChange={(e) => setFormData(prev => ({
              ...prev,
              meta: { ...prev.meta, title: e.target.value }
            }))}
            className="w-full p-2 border rounded"
          />
        </div>

        <div>
          <label className="block text-sm font-medium text-gray-700 mb-2">
            Meta Description
          </label>
          <textarea
            value={formData.meta.description}
            onChange={(e) => setFormData(prev => ({
              ...prev,
              meta: { ...prev.meta, description: e.target.value }
            }))}
            className="w-full p-2 border rounded"
            rows="3"
          />
        </div>

        <button
          type="submit"
          className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700"
        >
          {content ? 'Update Content' : 'Create Content'}
        </button>
      </form>
    </div>
  );
};

export default ContentManagement;
