import React, { useState, useEffect } from 'react';
import { toast } from 'react-toastify';
import { FaPlus, FaTrash } from 'react-icons/fa';
import { getContentByPage, updateContent } from '../../services/contentService';

const ContentManagement = () => {
  const [activeTab, setActiveTab] = useState('about');
  const [content, setContent] = useState({
    about: { title: '', description: '', features: [] },
    admissions: { title: '', description: '', steps: [] },
    countries: { title: '', description: '', countries: [] }
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        const [about, admissions, countries] = await Promise.all([
          getContentByPage('about'),
          getContentByPage('admissions'),
          getContentByPage('countries')
        ]);

        setContent({
          about: about || { title: '', description: '', features: [] },
          admissions: admissions || { title: '', description: '', steps: [] },
          countries: countries || { title: '', description: '', countries: [] }
        });
      } catch (error) {
        toast.error('Failed to fetch content');
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  const handleInputChange = (section, field, value) => {
    setContent(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: value
      }
    }));
  };

  const handleAddItem = (section, field) => {
    setContent(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: [...prev[section][field], '']
      }
    }));
  };

  const handleRemoveItem = (section, field, index) => {
    setContent(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: prev[section][field].filter((_, i) => i !== index)
      }
    }));
  };

  const handleItemChange = (section, field, index, value) => {
    setContent(prev => ({
      ...prev,
      [section]: {
        ...prev[section],
        [field]: prev[section][field].map((item, i) => 
          i === index ? value : item
        )
      }
    }));
  };

  const handleSubmit = async (section) => {
    try {
      await updateContent(section, content[section]);
      toast.success(`${section} content updated successfully`);
    } catch (error) {
      toast.error(`Failed to update ${section} content`);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <h1 className="text-2xl font-bold text-gray-800">Content Management</h1>

      <div className="bg-white rounded-lg shadow-md">
        <div className="border-b border-gray-200">
          <nav className="flex -mb-px">
            {['about', 'admissions', 'countries'].map((tab) => (
              <button
                key={tab}
                onClick={() => setActiveTab(tab)}
                className={`py-4 px-6 text-sm font-medium ${
                  activeTab === tab
                    ? 'border-b-2 border-blue-500 text-blue-600'
                    : 'text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {tab.charAt(0).toUpperCase() + tab.slice(1)}
              </button>
            ))}
          </nav>
        </div>

        <div className="p-6">
          {activeTab === 'about' && (
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Title</label>
                <input
                  type="text"
                  value={content.about.title}
                  onChange={(e) => handleInputChange('about', 'title', e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Description</label>
                <textarea
                  value={content.about.description}
                  onChange={(e) => handleInputChange('about', 'description', e.target.value)}
                  rows={4}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
              </div>
              <div>
                <div className="flex justify-between items-center">
                  <label className="block text-sm font-medium text-gray-700">Features</label>
                  <button
                    onClick={() => handleAddItem('about', 'features')}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    <FaPlus />
                  </button>
                </div>
                {content.about.features.map((feature, index) => (
                  <div key={index} className="flex items-center mt-2">
                    <input
                      type="text"
                      value={feature}
                      onChange={(e) => handleItemChange('about', 'features', index, e.target.value)}
                      className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    />
                    <button
                      onClick={() => handleRemoveItem('about', 'features', index)}
                      className="ml-2 text-red-500 hover:text-red-700"
                    >
                      <FaTrash />
                    </button>
                  </div>
                ))}
              </div>
              <button
                onClick={() => handleSubmit('about')}
                className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
              >
                Save Changes
              </button>
            </div>
          )}

          {activeTab === 'admissions' && (
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Title</label>
                <input
                  type="text"
                  value={content.admissions.title}
                  onChange={(e) => handleInputChange('admissions', 'title', e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Description</label>
                <textarea
                  value={content.admissions.description}
                  onChange={(e) => handleInputChange('admissions', 'description', e.target.value)}
                  rows={4}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
              </div>
              <div>
                <div className="flex justify-between items-center">
                  <label className="block text-sm font-medium text-gray-700">Steps</label>
                  <button
                    onClick={() => handleAddItem('admissions', 'steps')}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    <FaPlus />
                  </button>
                </div>
                {content.admissions.steps.map((step, index) => (
                  <div key={index} className="flex items-center mt-2">
                    <input
                      type="text"
                      value={step}
                      onChange={(e) => handleItemChange('admissions', 'steps', index, e.target.value)}
                      className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    />
                    <button
                      onClick={() => handleRemoveItem('admissions', 'steps', index)}
                      className="ml-2 text-red-500 hover:text-red-700"
                    >
                      <FaTrash />
                    </button>
                  </div>
                ))}
              </div>
              <button
                onClick={() => handleSubmit('admissions')}
                className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
              >
                Save Changes
              </button>
            </div>
          )}

          {activeTab === 'countries' && (
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-700">Title</label>
                <input
                  type="text"
                  value={content.countries.title}
                  onChange={(e) => handleInputChange('countries', 'title', e.target.value)}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
              </div>
              <div>
                <label className="block text-sm font-medium text-gray-700">Description</label>
                <textarea
                  value={content.countries.description}
                  onChange={(e) => handleInputChange('countries', 'description', e.target.value)}
                  rows={4}
                  className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                />
              </div>
              <div>
                <div className="flex justify-between items-center">
                  <label className="block text-sm font-medium text-gray-700">Countries</label>
                  <button
                    onClick={() => handleAddItem('countries', 'countries')}
                    className="text-blue-500 hover:text-blue-700"
                  >
                    <FaPlus />
                  </button>
                </div>
                {content.countries.countries.map((country, index) => (
                  <div key={index} className="flex items-center mt-2">
                    <input
                      type="text"
                      value={country}
                      onChange={(e) => handleItemChange('countries', 'countries', index, e.target.value)}
                      className="flex-1 rounded-md border-gray-300 shadow-sm focus:border-blue-500 focus:ring-blue-500"
                    />
                    <button
                      onClick={() => handleRemoveItem('countries', 'countries', index)}
                      className="ml-2 text-red-500 hover:text-red-700"
                    >
                      <FaTrash />
                    </button>
                  </div>
                ))}
              </div>
              <button
                onClick={() => handleSubmit('countries')}
                className="bg-blue-500 text-white px-4 py-2 rounded-md hover:bg-blue-600"
              >
                Save Changes
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ContentManagement;
