import React, { useState, useEffect } from 'react';
import { FaSpinner } from 'react-icons/fa';

// Default team members data if nothing is in localStorage
const defaultTeamMembers = [
  {
    _id: 'team-1',
    name: "Dr. Sarah Williams",
    role: "Founder & CEO",
    image: "https://images.unsplash.com/photo-1573496359142-b8d87734a5a2?ixlib=rb-4.0.3",
    bio: "With over 15 years of experience in education consulting, Sarah founded GradWay to help students achieve their academic dreams.",
    order: 1
  },
  {
    _id: 'team-2',
    name: "Michael Chen",
    role: "Admissions Specialist",
    image: "https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3",
    bio: "Michael specializes in university admissions and has helped hundreds of students secure spots in top universities worldwide.",
    order: 2
  },
  {
    _id: 'team-3',
    name: "Emma Wilson",
    role: "Visa Consultant",
    image: "https://images.unsplash.com/photo-1438761681033-6461ffad8d80?ixlib=rb-4.0.3",
    bio: "Emma's expertise in visa applications and immigration processes ensures smooth transitions for our students.",
    order: 3
  }
];

// Default about page data
const defaultAboutData = {
  hero: {
    title: "About GradWay",
    description: "We are dedicated to helping students achieve their academic dreams through personalized guidance and expert support."
  },
  mission: {
    title: "Our Mission",
    description1: "To provide comprehensive educational consulting services that empower students to achieve their academic goals and unlock their full potential.",
    description2: "We believe in personalized guidance, transparent processes, and unwavering support throughout every student's educational journey."
  },
  vision: {
    title: "Our Vision",
    description1: "To be the leading educational consultancy, recognized for our commitment to excellence and student success.",
    description2: "We envision a world where every student has access to quality education and the support they need to succeed."
  },
  team: {
    title: "Meet Our Team",
    description: "Our team of experienced professionals is dedicated to helping you achieve your academic goals."
  },
  cta: {
    title: "Ready to Start Your Journey?",
    description: "Contact us today to learn how we can help you achieve your academic goals.",
    buttonText: "Get in Touch",
    buttonLink: "/contact"
  }
};

const AboutPage = () => {
  const [aboutData, setAboutData] = useState(defaultAboutData);
  const [teamMembers, setTeamMembers] = useState(defaultTeamMembers);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch about page data from localStorage
  useEffect(() => {
    const fetchAboutPageData = () => {
      try {
        setLoading(true);
        // Get about page data from localStorage
        const storedAboutPage = JSON.parse(localStorage.getItem('aboutPageDatabase'));

        if (storedAboutPage) {
          setAboutData({
            hero: storedAboutPage.hero || defaultAboutData.hero,
            mission: storedAboutPage.mission || defaultAboutData.mission,
            vision: storedAboutPage.vision || defaultAboutData.vision,
            team: storedAboutPage.team || defaultAboutData.team,
            cta: storedAboutPage.cta || defaultAboutData.cta
          });

          // Get team members
          if (storedAboutPage.teamMembers && storedAboutPage.teamMembers.length > 0) {
            // Sort by order if available
            const sortedTeamMembers = [...storedAboutPage.teamMembers].sort((a, b) => (a.order || 0) - (b.order || 0));
            setTeamMembers(sortedTeamMembers);
          } else {
            setTeamMembers(defaultTeamMembers);
          }
        } else {
          // Use default data if nothing is in localStorage
          setAboutData(defaultAboutData);
          setTeamMembers(defaultTeamMembers);
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching about page data:', err);
        setError('Failed to load about page data');
        setAboutData(defaultAboutData);
        setTeamMembers(defaultTeamMembers);
        setLoading(false);
      }
    };

    // Initial fetch
    fetchAboutPageData();

    // Set up storage event listener to detect changes from other tabs/windows
    const handleStorageChange = (e) => {
      if (e.key === 'aboutPageDatabase') {
        console.log('About page database updated, refreshing...');
        fetchAboutPageData();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Poll for changes every 2 seconds (as a backup for changes in the same tab)
    const intervalId = setInterval(fetchAboutPageData, 2000);

    // Cleanup
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      clearInterval(intervalId);
    };
  }, []);
  return (
    <div className="pt-16">
      {/* Hero Section */}
      <section className="relative py-20">
        <div className="absolute inset-0 bg-navy/90"></div>
        <div className="container mx-auto px-4 relative z-10">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-6">
            About GradWay
          </h1>
          <p className="text-xl text-gray-200 max-w-3xl">
            We are dedicated to helping students achieve their academic dreams through personalized guidance and expert support.
          </p>
        </div>
      </section>

      {/* Mission & Vision */}
      <section className="py-20">
        <div className="container mx-auto px-4">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
            <div>
              <h2 className="text-3xl font-bold text-navy mb-6">Our Mission</h2>
              <p className="text-gray-600 mb-6">
                To provide comprehensive educational consulting services that empower students to achieve their academic goals and unlock their full potential.
              </p>
              <p className="text-gray-600">
                We believe in personalized guidance, transparent processes, and unwavering support throughout every student's educational journey.
              </p>
            </div>
            <div>
              <h2 className="text-3xl font-bold text-navy mb-6">Our Vision</h2>
              <p className="text-gray-600 mb-6">
                To be the leading educational consultancy, recognized for our commitment to excellence and student success.
              </p>
              <p className="text-gray-600">
                We envision a world where every student has access to quality education and the support they need to succeed.
              </p>
            </div>
          </div>
        </div>
      </section>

      {/* Team Section */}
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
              Meet Our Team
            </h2>
            <p className="text-gray-600 max-w-2xl mx-auto">
              Our team of experienced professionals is dedicated to helping you achieve your academic goals.
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {teamMembers.map((member, index) => (
              <div
                key={index}
                className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow"
              >
                <div className="w-32 h-32 rounded-full overflow-hidden mx-auto mb-6">
                  <img
                    src={member.image}
                    alt={member.name}
                    className="w-full h-full object-cover"
                  />
                </div>
                <h3 className="text-xl font-semibold text-navy text-center mb-2">
                  {member.name}
                </h3>
                <p className="text-gray-600 text-center mb-4">{member.role}</p>
                <p className="text-gray-600 text-center">{member.bio}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-navy text-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl md:text-4xl font-bold mb-6">
            Ready to Start Your Journey?
          </h2>
          <p className="text-xl text-gray-200 mb-8 max-w-2xl mx-auto">
            Contact us today to learn how we can help you achieve your academic goals.
          </p>
          <a
            href="/contact"
            className="inline-block px-8 py-4 bg-white text-navy font-semibold rounded-md hover:bg-gray-100 transition-colors"
          >
            Get in Touch
          </a>
        </div>
      </section>
    </div>
  );
};

export default AboutPage;
