import React, { useState, useEffect } from 'react';
import { FaGraduationCap, FaPassport, FaUserTie, FaLanguage, FaHome, FaHandshake, FaBriefcase, FaPlane, FaUniversity, FaMoneyBillWave } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import LoadingSpinner from '../shared/LoadingSpinner';
import ErrorMessage from '../shared/ErrorMessage';

// Map service icon names to actual icon components
const iconMap = {
  'graduation-cap': <FaGraduationCap className="w-8 h-8" />,
  'passport': <FaPassport className="w-8 h-8" />,
  'user-tie': <FaUserTie className="w-8 h-8" />,
  'language': <FaLanguage className="w-8 h-8" />,
  'home': <FaHome className="w-8 h-8" />,
  'handshake': <FaHandshake className="w-8 h-8" />,
  'briefcase': <FaBriefcase className="w-8 h-8" />,
  'plane': <FaPlane className="w-8 h-8" />,
  'university': <FaUniversity className="w-8 h-8" />,
  'money-bill-wave': <FaMoneyBillWave className="w-8 h-8" />
};

const Services = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchServices = () => {
      try {
        setLoading(true);

        // Try to get services from both possible localStorage keys
        // First check AdminDataServices.js storage
        let storedServices = JSON.parse(localStorage.getItem('servicesDatabase')) || [];

        // If no services found, create some default ones for testing
        if (storedServices.length === 0) {
          console.log('No services found in localStorage, creating defaults');
          storedServices = [
            {
              _id: 'service-default-1',
              title: 'University Admissions',
              description: 'Expert guidance in selecting and applying to top universities worldwide.',
              icon: 'graduation-cap',
              featured: true
            },
            {
              _id: 'service-default-2',
              title: 'Visa Assistance',
              description: 'Comprehensive assistance with student visa applications and documentation.',
              icon: 'passport',
              featured: true
            },
            {
              _id: 'service-default-3',
              title: 'Career Counseling',
              description: 'Professional guidance to help you make informed decisions about your career path.',
              icon: 'briefcase',
              featured: false
            }
          ];
        }

        // Sort by featured and limit to 6 services
        const sortedServices = storedServices
          .filter(service => service.isActive !== false) // Only show active services
          .sort((a, b) => (b.featured === a.featured) ? 0 : b.featured ? 1 : -1)
          .slice(0, 6);

        setServices(sortedServices);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching services:', err);
        setError('Failed to load services');
        setLoading(false);
      }
    };

    // Initial fetch
    fetchServices();

    // Set up storage event listener to detect changes from other tabs/windows
    const handleStorageChange = (e) => {
      if (e.key === 'servicesDatabase') {
        console.log('Services database updated, refreshing...');
        fetchServices();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Poll for changes every 2 seconds (as a backup for changes in the same tab)
    const intervalId = setInterval(fetchServices, 2000);

    // Cleanup
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      clearInterval(intervalId);
    };
  }, []);
  if (loading) {
    return (
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-4 text-center">
          <LoadingSpinner />
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-4 text-center">
          <ErrorMessage message={error} />
        </div>
      </section>
    );
  }

  // If no services are available, show a message
  if (services.length === 0) {
    return (
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Our Services
          </h2>
          <p className="text-gray-600">
            No services available at the moment. Please check back later.
          </p>
        </div>
      </section>
    );
  }

  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
            Our Services
          </h2>
          <p className="text-gray-600 max-w-2xl mx-auto">
            Comprehensive support for your international education journey
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {services.map((service, index) => {
            // Get the icon from the map or use a default icon
            const serviceIcon = iconMap[service.icon] || <FaGraduationCap className="w-8 h-8" />;

            return (
              <div
                key={service._id || index}
                className="bg-white p-8 rounded-lg shadow-sm hover:shadow-md transition-shadow duration-300"
              >
                <div className="text-navy mb-4">
                  {serviceIcon}
                </div>
                <h3 className="text-xl font-semibold text-navy mb-3">
                  {service.title}
                </h3>
                <p className="text-gray-600 mb-4">
                  {service.description}
                </p>
                {service.featured && (
                  <span className="inline-block bg-gold text-navy text-xs font-bold px-2 py-1 rounded mb-3">
                    Featured
                  </span>
                )}
                <Link
                  to="/services"
                  className="text-navy font-medium inline-flex items-center hover:text-navy-light transition-colors duration-300"
                >
                  Learn More
                  <svg className="w-4 h-4 ml-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                  </svg>
                </Link>
              </div>
            );
          })}
        </div>
      </div>
    </section>
  );
};

export default Services;
