import React from 'react';
import { Link } from 'react-router-dom';

const Hero = ({ content }) => {
  // Find title, subtitle and description fields from content
  const getFieldValue = (fieldName) => {
    if (!content || !content.fields) return '';
    const field = content.fields.find(f => f.name === fieldName);
    return field ? field.value : '';
  };

  const title = getFieldValue('title');
  const subtitle = getFieldValue('subtitle');
  const description = getFieldValue('description');
  return (
    <section className="relative min-h-screen flex items-center">
      {/* Background Image with Overlay */}
      <div className="absolute inset-0">
        <img
          src="https://images.unsplash.com/photo-1523050854058-8df90110c9f1?ixlib=rb-4.0.3"
          alt="Students studying"
          className="w-full h-full object-cover"
        />
        <div className="absolute inset-0 bg-black/60"></div>
      </div>

      {/* Content */}
      <div className="container mx-auto px-4 relative z-10">
        <div className="max-w-3xl">
          <h1 className="text-4xl md:text-5xl lg:text-6xl font-bold text-white mb-6">
            {title || 'Empowering Your Education Abroad'}
          </h1>

          <p className="text-lg md:text-xl text-gray-200 mb-3">
            {subtitle || 'Expert guidance for international education'}
          </p>

          <p className="text-xl text-gray-200 mb-8">
            {description || "Expert guidance and support for your international education journey. From university selection to visa processing, we're here to help you achieve your academic goals."}
          </p>

          <Link
            to="/contact"
            className="inline-block px-8 py-3 bg-navy text-white font-semibold rounded-md hover:bg-navy-light transition-colors duration-300"
          >
            Get in Touch
          </Link>
        </div>

        {/* Features */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-8 mt-20">
          <Feature icon="✓" title="Licensed and Qualified staff" />
          <Feature icon="✓" title="100% Free Counselling and Assessment" />
          <Feature icon="✓" title="Free Application and Admission" />
          <Feature icon="✓" title="24/7 Student Support Guidance" />
        </div>
      </div>
    </section>
  );
};

const Feature = ({ icon, title }) => (
  <div className="bg-white/10 backdrop-blur-sm p-6 rounded-lg">
    <div className="text-2xl text-white mb-2">{icon}</div>
    <h3 className="text-white font-medium">{title}</h3>
  </div>
);

export default Hero;
