import React, { useState, useEffect } from 'react';
import { FaPlus, FaEdit, FaTrash, FaArrowUp, FaArrowDown, FaSave } from 'react-icons/fa';
import { toast } from 'react-toastify';
import { ServicesPageService } from './AdminDataServices';

const ServicesPageManagement = () => {
  // State for services
  const [services, setServices] = useState([]);
  const [selectedService, setSelectedService] = useState(null);
  const [serviceFormMode, setServiceFormMode] = useState('add'); // 'add' or 'edit'
  const [serviceFormData, setServiceFormData] = useState({
    id: '',
    iconName: 'FaGraduationCap',
    title: '',
    description: '',
    features: ['']
  });

  // State for hero section
  const [heroData, setHeroData] = useState({
    title: '',
    description: ''
  });

  // State for process section
  const [processData, setProcessData] = useState({
    title: '',
    description: '',
    steps: [
      { number: 1, title: '', description: '' },
      { number: 2, title: '', description: '' },
      { number: 3, title: '', description: '' },
      { number: 4, title: '', description: '' }
    ]
  });

  // State for CTA section
  const [ctaData, setCtaData] = useState({
    title: '',
    description: '',
    buttonText: '',
    buttonLink: ''
  });

  const [loading, setLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('services'); // 'services', 'hero', 'process', 'cta'

  // Available icons
  const availableIcons = [
    { name: 'FaGraduationCap', label: 'Graduation Cap' },
    { name: 'FaPassport', label: 'Passport' },
    { name: 'FaUniversity', label: 'University' },
    { name: 'FaLanguage', label: 'Language' }
  ];

  // Fetch services page data on component mount
  useEffect(() => {
    fetchServicesPageData();
  }, []);

  // Fetch services page data
  const fetchServicesPageData = async () => {
    try {
      setLoading(true);
      const data = await ServicesPageService.getServicesPage();

      // Set services
      const sortedServices = [...data.services].sort((a, b) => (a.order || 0) - (b.order || 0));
      setServices(sortedServices);

      // Set hero data
      setHeroData(data.hero);

      // Set process data
      setProcessData(data.process);

      // Set CTA data
      setCtaData(data.cta);

      setLoading(false);
    } catch (error) {
      console.error('Error fetching services page data:', error);
      toast.error('Failed to load services page data');
      setLoading(false);
    }
  };

  // Handle service form input changes
  const handleServiceInputChange = (e) => {
    const { name, value } = e.target;
    setServiceFormData({
      ...serviceFormData,
      [name]: value
    });
  };

  // Handle feature input changes
  const handleFeatureChange = (index, value) => {
    const updatedFeatures = [...serviceFormData.features];
    updatedFeatures[index] = value;
    setServiceFormData({
      ...serviceFormData,
      features: updatedFeatures
    });
  };

  // Add new feature field
  const addFeatureField = () => {
    setServiceFormData({
      ...serviceFormData,
      features: [...serviceFormData.features, '']
    });
  };

  // Remove feature field
  const removeFeatureField = (index) => {
    const updatedFeatures = [...serviceFormData.features];
    updatedFeatures.splice(index, 1);
    setServiceFormData({
      ...serviceFormData,
      features: updatedFeatures
    });
  };

  // Reset service form
  const resetServiceForm = () => {
    setServiceFormData({
      id: '',
      iconName: 'FaGraduationCap',
      title: '',
      description: '',
      features: ['']
    });
    setSelectedService(null);
    setServiceFormMode('add');
  };

  // Handle service form submission
  const handleServiceSubmit = async (e) => {
    e.preventDefault();

    try {
      // Filter out empty features
      const cleanedFormData = {
        ...serviceFormData,
        features: serviceFormData.features.filter(feature => feature.trim() !== '')
      };

      if (serviceFormMode === 'add') {
        await ServicesPageService.addService(cleanedFormData);
      } else {
        await ServicesPageService.updateService(selectedService._id, cleanedFormData);
      }

      resetServiceForm();
      fetchServicesPageData();
    } catch (error) {
      console.error('Error saving service:', error);
      toast.error('Failed to save service');
    }
  };

  // Edit service
  const handleEditService = (service) => {
    setSelectedService(service);
    setServiceFormData({
      id: service.id || '',
      iconName: service.iconName || 'FaGraduationCap',
      title: service.title || '',
      description: service.description || '',
      features: service.features.length > 0 ? [...service.features] : ['']
    });
    setServiceFormMode('edit');
  };

  // Delete service
  const handleDeleteService = async (id) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      try {
        await ServicesPageService.deleteService(id);
        fetchServicesPageData();
      } catch (error) {
        console.error('Error deleting service:', error);
        toast.error('Failed to delete service');
      }
    }
  };

  // Move service up in order
  const handleMoveServiceUp = async (index) => {
    if (index === 0) return; // Already at the top

    try {
      const newServices = [...services];
      [newServices[index - 1], newServices[index]] = [newServices[index], newServices[index - 1]];

      // Get array of IDs in the new order
      const orderedIds = newServices.map(service => service._id);

      await ServicesPageService.reorderServices(orderedIds);
      fetchServicesPageData();
    } catch (error) {
      console.error('Error reordering services:', error);
      toast.error('Failed to reorder services');
    }
  };

  // Move service down in order
  const handleMoveServiceDown = async (index) => {
    if (index === services.length - 1) return; // Already at the bottom

    try {
      const newServices = [...services];
      [newServices[index], newServices[index + 1]] = [newServices[index + 1], newServices[index]];

      // Get array of IDs in the new order
      const orderedIds = newServices.map(service => service._id);

      await ServicesPageService.reorderServices(orderedIds);
      fetchServicesPageData();
    } catch (error) {
      console.error('Error reordering services:', error);
      toast.error('Failed to reorder services');
    }
  };

  // Handle hero data input changes
  const handleHeroInputChange = (e) => {
    const { name, value } = e.target;
    setHeroData({
      ...heroData,
      [name]: value
    });
  };

  // Save hero data
  const handleSaveHero = async (e) => {
    e.preventDefault();

    try {
      await ServicesPageService.updateHero(heroData);
      toast.success('Hero section updated successfully');
    } catch (error) {
      console.error('Error saving hero data:', error);
      toast.error('Failed to save hero data');
    }
  };

  // Handle process data input changes
  const handleProcessInputChange = (e) => {
    const { name, value } = e.target;
    setProcessData({
      ...processData,
      [name]: value
    });
  };

  // Handle process step input changes
  const handleProcessStepChange = (index, field, value) => {
    const updatedSteps = [...processData.steps];
    updatedSteps[index] = {
      ...updatedSteps[index],
      [field]: field === 'number' ? parseInt(value, 10) : value
    };
    setProcessData({
      ...processData,
      steps: updatedSteps
    });
  };

  // Save process data
  const handleSaveProcess = async (e) => {
    e.preventDefault();

    try {
      await ServicesPageService.updateProcess(processData);
      toast.success('Process section updated successfully');
    } catch (error) {
      console.error('Error saving process data:', error);
      toast.error('Failed to save process data');
    }
  };

  // Handle CTA data input changes
  const handleCtaInputChange = (e) => {
    const { name, value } = e.target;
    setCtaData({
      ...ctaData,
      [name]: value
    });
  };

  // Save CTA data
  const handleSaveCta = async (e) => {
    e.preventDefault();

    try {
      await ServicesPageService.updateCta(ctaData);
      toast.success('CTA section updated successfully');
    } catch (error) {
      console.error('Error saving CTA data:', error);
      toast.error('Failed to save CTA data');
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold text-navy mb-8">Services Page Management</h1>

      {/* Tabs */}
      <div className="flex border-b mb-8">
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'services' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('services')}
        >
          Services
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'hero' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('hero')}
        >
          Hero Section
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'process' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('process')}
        >
          Process Section
        </button>
        <button
          className={`py-2 px-4 font-medium ${activeTab === 'cta' ? 'border-b-2 border-navy text-navy' : 'text-gray-500'}`}
          onClick={() => setActiveTab('cta')}
        >
          CTA Section
        </button>
      </div>

      {loading ? (
        <div className="text-center py-8">
          <p>Loading...</p>
        </div>
      ) : (
        <>
          {/* Services Tab */}
          {activeTab === 'services' && (
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
              {/* Service Form */}
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold mb-4">
                  {serviceFormMode === 'add' ? 'Add New Service' : 'Edit Service'}
                </h2>

                <form onSubmit={handleServiceSubmit}>
                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="id">
                      Service ID
                    </label>
                    <input
                      type="text"
                      id="id"
                      name="id"
                      value={serviceFormData.id}
                      onChange={handleServiceInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      placeholder="e.g. study-abroad"
                      required
                    />
                    <p className="text-sm text-gray-500 mt-1">
                      Unique identifier for the service (no spaces, use hyphens)
                    </p>
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="iconName">
                      Icon
                    </label>
                    <select
                      id="iconName"
                      name="iconName"
                      value={serviceFormData.iconName}
                      onChange={handleServiceInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      required
                    >
                      {availableIcons.map((icon) => (
                        <option key={icon.name} value={icon.name}>
                          {icon.label}
                        </option>
                      ))}
                    </select>
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="title">
                      Title
                    </label>
                    <input
                      type="text"
                      id="title"
                      name="title"
                      value={serviceFormData.title}
                      onChange={handleServiceInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      placeholder="e.g. Study Abroad Consultation"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="description">
                      Description
                    </label>
                    <textarea
                      id="description"
                      name="description"
                      value={serviceFormData.description}
                      onChange={handleServiceInputChange}
                      className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                      placeholder="Brief description of the service"
                      rows="3"
                      required
                    />
                  </div>

                  <div className="mb-4">
                    <label className="block text-gray-700 text-sm font-bold mb-2">
                      Features
                    </label>
                    {serviceFormData.features.map((feature, index) => (
                      <div key={index} className="flex items-center mb-2">
                        <input
                          type="text"
                          value={feature}
                          onChange={(e) => handleFeatureChange(index, e.target.value)}
                          className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                          placeholder={`Feature ${index + 1}`}
                        />
                        <button
                          type="button"
                          onClick={() => removeFeatureField(index)}
                          className="ml-2 text-red-500 hover:text-red-700"
                          disabled={serviceFormData.features.length <= 1}
                        >
                          <FaTrash />
                        </button>
                      </div>
                    ))}
                    <button
                      type="button"
                      onClick={addFeatureField}
                      className="text-sm text-blue-500 hover:text-blue-700 mt-2"
                    >
                      + Add Feature
                    </button>
                  </div>

                  <div className="flex items-center justify-between">
                    <button
                      type="submit"
                      className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                    >
                      {serviceFormMode === 'add' ? 'Add Service' : 'Update Service'}
                    </button>

                    {serviceFormMode === 'edit' && (
                      <button
                        type="button"
                        onClick={resetServiceForm}
                        className="bg-gray-300 hover:bg-gray-400 text-gray-800 font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                      >
                        Cancel
                      </button>
                    )}
                  </div>
                </form>
              </div>

              {/* Services List */}
              <div className="bg-white rounded-lg shadow-md p-6">
                <h2 className="text-xl font-semibold mb-4">Services List</h2>

                {services.length === 0 ? (
                  <p className="text-center py-4">No services found. Add your first service!</p>
                ) : (
                  <div className="space-y-4">
                    {services.map((service, index) => (
                      <div 
                        key={service._id} 
                        className="border rounded-lg p-4 flex items-center justify-between bg-gray-50 hover:bg-gray-100"
                      >
                        <div>
                          <h3 className="font-semibold">{service.title}</h3>
                          <p className="text-sm text-gray-600 truncate max-w-xs">
                            {service.description}
                          </p>
                        </div>

                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => handleMoveServiceUp(index)}
                            disabled={index === 0}
                            className={`p-2 rounded-full ${index === 0 ? 'text-gray-300 cursor-not-allowed' : 'text-gray-600 hover:bg-gray-200'}`}
                            title="Move Up"
                          >
                            <FaArrowUp />
                          </button>
                          <button
                            onClick={() => handleMoveServiceDown(index)}
                            disabled={index === services.length - 1}
                            className={`p-2 rounded-full ${index === services.length - 1 ? 'text-gray-300 cursor-not-allowed' : 'text-gray-600 hover:bg-gray-200'}`}
                            title="Move Down"
                          >
                            <FaArrowDown />
                          </button>
                          <button
                            onClick={() => handleEditService(service)}
                            className="p-2 rounded-full text-blue-600 hover:bg-blue-100"
                            title="Edit"
                          >
                            <FaEdit />
                          </button>
                          <button
                            onClick={() => handleDeleteService(service._id)}
                            className="p-2 rounded-full text-red-600 hover:bg-red-100"
                            title="Delete"
                          >
                            <FaTrash />
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            </div>
          )}

          {/* Hero Section Tab */}
          {activeTab === 'hero' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Hero Section</h2>

              <form onSubmit={handleSaveHero}>
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="heroTitle">
                    Title
                  </label>
                  <input
                    type="text"
                    id="heroTitle"
                    name="title"
                    value={heroData.title}
                    onChange={handleHeroInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="heroDescription">
                    Description
                  </label>
                  <textarea
                    id="heroDescription"
                    name="description"
                    value={heroData.description}
                    onChange={handleHeroInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    rows="3"
                    required
                  />
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save Hero Section
                </button>
              </form>

              {/* Preview */}
              <div className="mt-8">
                <h3 className="text-lg font-semibold mb-2">Preview</h3>
                <div className="bg-navy text-white p-6 rounded-lg">
                  <h1 className="text-2xl font-bold mb-2">{heroData.title}</h1>
                  <p>{heroData.description}</p>
                </div>
              </div>
            </div>
          )}

          {/* Process Section Tab */}
          {activeTab === 'process' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">Process Section</h2>

              <form onSubmit={handleSaveProcess}>
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="processTitle">
                    Title
                  </label>
                  <input
                    type="text"
                    id="processTitle"
                    name="title"
                    value={processData.title}
                    onChange={handleProcessInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="processDescription">
                    Description
                  </label>
                  <textarea
                    id="processDescription"
                    name="description"
                    value={processData.description}
                    onChange={handleProcessInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    rows="2"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2">
                    Process Steps
                  </label>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {processData.steps.map((step, index) => (
                      <div key={index} className="border rounded-lg p-4">
                        <div className="mb-2">
                          <label className="block text-gray-700 text-sm font-bold mb-1">
                            Step {index + 1} - Title
                          </label>
                          <input
                            type="text"
                            value={step.title}
                            onChange={(e) => handleProcessStepChange(index, 'title', e.target.value)}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            required
                          />
                        </div>
                        <div>
                          <label className="block text-gray-700 text-sm font-bold mb-1">
                            Step {index + 1} - Description
                          </label>
                          <input
                            type="text"
                            value={step.description}
                            onChange={(e) => handleProcessStepChange(index, 'description', e.target.value)}
                            className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                            required
                          />
                        </div>
                      </div>
                    ))}
                  </div>
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save Process Section
                </button>
              </form>

              {/* Preview */}
              <div className="mt-8">
                <h3 className="text-lg font-semibold mb-2">Preview</h3>
                <div className="bg-gray-50 p-6 rounded-lg">
                  <div className="text-center mb-4">
                    <h2 className="text-xl font-bold text-navy mb-2">{processData.title}</h2>
                    <p className="text-gray-600">{processData.description}</p>
                  </div>
                  <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
                    {processData.steps.map((step) => (
                      <div key={step.number} className="text-center">
                        <div className="w-12 h-12 bg-navy text-white rounded-full flex items-center justify-center mx-auto mb-2">
                          {step.number}
                        </div>
                        <h3 className="font-semibold text-navy mb-1">{step.title}</h3>
                        <p className="text-sm text-gray-600">{step.description}</p>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* CTA Section Tab */}
          {activeTab === 'cta' && (
            <div className="bg-white rounded-lg shadow-md p-6">
              <h2 className="text-xl font-semibold mb-4">CTA Section</h2>

              <form onSubmit={handleSaveCta}>
                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="ctaTitle">
                    Title
                  </label>
                  <input
                    type="text"
                    id="ctaTitle"
                    name="title"
                    value={ctaData.title}
                    onChange={handleCtaInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="ctaDescription">
                    Description
                  </label>
                  <textarea
                    id="ctaDescription"
                    name="description"
                    value={ctaData.description}
                    onChange={handleCtaInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    rows="3"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="ctaButtonText">
                    Button Text
                  </label>
                  <input
                    type="text"
                    id="ctaButtonText"
                    name="buttonText"
                    value={ctaData.buttonText}
                    onChange={handleCtaInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    required
                  />
                </div>

                <div className="mb-4">
                  <label className="block text-gray-700 text-sm font-bold mb-2" htmlFor="ctaButtonLink">
                    Button Link
                  </label>
                  <input
                    type="text"
                    id="ctaButtonLink"
                    name="buttonLink"
                    value={ctaData.buttonLink}
                    onChange={handleCtaInputChange}
                    className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
                    placeholder="e.g. /contact"
                    required
                  />
                </div>

                <button
                  type="submit"
                  className="bg-navy hover:bg-navy-dark text-white font-bold py-2 px-4 rounded focus:outline-none focus:shadow-outline"
                >
                  <FaSave className="inline mr-2" />
                  Save CTA Section
                </button>
              </form>

              {/* Preview */}
              <div className="mt-8">
                <h3 className="text-lg font-semibold mb-2">Preview</h3>
                <div className="bg-navy text-white p-6 rounded-lg text-center">
                  <h2 className="text-xl font-bold mb-2">{ctaData.title}</h2>
                  <p className="mb-4">{ctaData.description}</p>
                  <button className="bg-white text-navy font-bold py-2 px-4 rounded">
                    {ctaData.buttonText}
                  </button>
                </div>
              </div>
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default ServicesPageManagement;
