import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import LoadingSpinner from '../shared/LoadingSpinner';
import ErrorMessage from '../shared/ErrorMessage';
import { motion } from 'framer-motion';

const AboutSection = () => {
  const [aboutData, setAboutData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAboutData = () => {
      try {
        setLoading(true);
        // Get about data from localStorage
        const storedAboutData = JSON.parse(localStorage.getItem('aboutDatabase'));

        if (storedAboutData) {
          setAboutData(storedAboutData);
        } else {
          // Default data if nothing is in localStorage
          setAboutData({
            title: 'About STEPS Education Limited',
            description1: 'Established in 2020, STEPS Education Limited is committed to providing exceptional educational services to international students. Our mission is guided by our core values: Education | Training | Excellence.',
            description2: 'We aim to help only 50 students per intake to maintain our service quality, ensuring personalized attention and support throughout your educational journey. Our team of dedicated specialists is led by Mr. Mohsin, who brings years of expertise in international student recruitment and visa processing.',
            image: 'https://images.unsplash.com/photo-1522202176988-66273c2fd55f?ixlib=rb-4.0.3',
            yearsExperience: 'EST',
            yearsExperienceValue: '2020',
            values: [
              { title: 'INNOVATIVE TRAINING', description: 'We provide cutting-edge training programs to prepare students for academic success' },
              { title: 'SMART MANAGEMENT', description: 'Our efficient processes ensure timely applications and assessments' },
              { title: '50 STUDENTS PER INTAKE', description: 'Limited intake to maintain high-quality service and personalized attention' }
            ]
          });
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching about data:', err);
        setError('Failed to load about section');
        setLoading(false);
      }
    };

    // Initial fetch
    fetchAboutData();

    // Set up storage event listener to detect changes from other tabs/windows
    const handleStorageChange = (e) => {
      if (e.key === 'aboutDatabase') {
        console.log('About database updated, refreshing...');
        fetchAboutData();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Poll for changes every 2 seconds (as a backup for changes in the same tab)
    const intervalId = setInterval(fetchAboutData, 2000);

    // Cleanup
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      clearInterval(intervalId);
    };
  }, []);

  if (loading) {
    return (
      <section className="py-20">
        <div className="container mx-auto px-4 text-center">
          <LoadingSpinner />
        </div>
      </section>
    );
  }

  if (error) {
    return (
      <section className="py-20">
        <div className="container mx-auto px-4 text-center">
          <ErrorMessage message={error} />
        </div>
      </section>
    );
  }

  // If no about data is available, don't render anything
  if (!aboutData) return null;

  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <motion.div 
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.6 }}
          viewport={{ once: true }}
          className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center"
        >
          {/* Image */}
          <div className="relative">
            <div className="aspect-w-4 aspect-h-3 rounded-lg overflow-hidden shadow-xl">
              <img
                src={aboutData.image}
                alt="STEPS Education Team"
                className="w-full h-full object-cover"
              />
            </div>
            <motion.div 
              initial={{ y: 20, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.3, duration: 0.5 }}
              viewport={{ once: true }}
              className="absolute -bottom-6 -right-6 bg-blue-600 text-white p-6 rounded-lg shadow-lg"
            >
              <div className="text-sm font-medium">{aboutData.yearsExperience}</div>
              <div className="text-3xl font-bold">{aboutData.yearsExperienceValue}</div>
            </motion.div>
          </div>

          {/* Content */}
          <div>
            <motion.div
              initial={{ y: 20, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ duration: 0.5 }}
              viewport={{ once: true }}
            >
              <h2 className="text-3xl md:text-4xl font-bold text-blue-600 mb-2">
                {aboutData.title}
              </h2>
              <div className="w-20 h-1 bg-blue-600 mb-6"></div>
              <p className="text-gray-700 mb-4">
                {aboutData.description1}
              </p>
              <p className="text-gray-700 mb-8">
                {aboutData.description2}
              </p>
            </motion.div>

            {/* Values */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
              {aboutData.values.map((value, index) => (
                <motion.div 
                  key={index}
                  initial={{ y: 20, opacity: 0 }}
                  whileInView={{ y: 0, opacity: 1 }}
                  transition={{ delay: 0.2 * index, duration: 0.5 }}
                  viewport={{ once: true }}
                  className="bg-white p-4 rounded-lg shadow-md"
                >
                  <h3 className="text-blue-600 font-semibold mb-2">{value.title}</h3>
                  <p className="text-gray-600 text-sm">{value.description}</p>
                </motion.div>
              ))}
            </div>

            <motion.div 
              initial={{ y: 20, opacity: 0 }}
              whileInView={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.6, duration: 0.5 }}
              viewport={{ once: true }}
              className="flex flex-col sm:flex-row gap-4"
            >
              <Link
                to="/about"
                className="px-8 py-3 bg-blue-600 text-white font-semibold rounded-md hover:bg-blue-700 transition-colors text-center"
              >
                Why Choose Us
              </Link>
              <Link
                to="/contact"
                className="px-8 py-3 border-2 border-blue-600 text-blue-600 font-semibold rounded-md hover:bg-blue-50 transition-colors text-center"
              >
                Contact Mr. Mohsin
              </Link>
            </motion.div>
          </div>
        </motion.div>
      </div>
    </section>
  );
};

export default AboutSection;
