import axios from 'axios';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

// Configure axios defaults
axios.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => {
    return Promise.reject(error);
  }
);

const handleError = (error) => {
  if (error.response) {
    throw new Error(error.response.data.error || 'Server error');
  } else if (error.request) {
    throw new Error('Could not connect to server');
  } else {
    throw new Error('Error setting up the request');
  }
};

// Course related functions
export const getCourses = async () => {
  try {
    const response = await axios.get(`${API_URL}/admin/courses`);
    return response.data.courses;
  } catch (error) {
    handleError(error);
    return [];
  }
};

export const createCourse = async (courseData) => {
  try {
    const response = await axios.post(`${API_URL}/admin/courses`, courseData);
    return response.data.course;
  } catch (error) {
    handleError(error);
  }
};

export const updateCourse = async (id, courseData) => {
  try {
    const response = await axios.put(`${API_URL}/admin/courses/${id}`, courseData);
    return response.data.course;
  } catch (error) {
    handleError(error);
  }
};

export const deleteCourse = async (id) => {
  try {
    await axios.delete(`${API_URL}/admin/courses/${id}`);
