import React, { useState, useEffect } from 'react';
import { FaFacebook, FaTwitter, FaInstagram, FaLinkedin, FaEnvelope, FaPhone, FaMapMarkerAlt, FaGraduationCap } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { ROUTES } from '../../constants/routes';
import { useTheme } from '../../context/ThemeContext';

const Footer = () => {
  const [footerData, setFooterData] = useState({
    companyInfo: {
      name: 'STEPS Education Limited',
      description: 'We aim to help only 50 students per intake to maintain our service quality. Education | Training | Excellence.',
      socialLinks: [
        { platform: 'facebook', url: 'https://facebook.com/stepseducation' },
        { platform: 'twitter', url: 'https://twitter.com/stepseducation' },
        { platform: 'instagram', url: 'https://instagram.com/stepseducation' },
        { platform: 'linkedin', url: 'https://linkedin.com/company/stepseducation' }
      ]
    },
    quickLinks: [
      { name: 'Home', url: '/' },
      { name: 'About Us', url: '/about' },
      { name: 'Services', url: '/services' },
      { name: 'Admission Process', url: '/admission-process' },
      { name: 'CV & Jobs Portal', url: '/cv-jobs' },
      { name: 'Contact', url: '/contact' }
    ],
    services: [
      { name: 'International Student Recruitment', url: '/services#recruitment' },
      { name: 'Documents Assessment', url: '/services#assessment' },
      { name: 'University Selection', url: '/services#university' },
      { name: 'Visa Application Guidance', url: '/services#visa' },
      { name: 'Local Student Admissions', url: '/services#local' },
      { name: 'Employability Services', url: '/services#employability' }
    ],
    contactInfo: {
      address: 'City Gate House, Suite 504, London E7 9HZ',
      phone: '+44-475-63228537',
      email: 'info@stepseeducation.co.uk'
    },
    copyright: 'Â© {year} STEPS Education Limited. All rights reserved.'
  });

  useEffect(() => {
    // Fetch footer data from localStorage
    const fetchFooterData = () => {
      try {
        const data = JSON.parse(localStorage.getItem('footerDatabase'));
        if (data) {
          setFooterData(data);
        }
      } catch (error) {
        console.error('Error fetching footer data:', error);
      }
    };

    fetchFooterData();

    // Set up event listener for storage changes
    const handleStorageChange = (e) => {
      if (e.key === 'footerDatabase') {
        fetchFooterData();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Helper function to render social media icons
  const renderSocialIcon = (platform) => {
    switch (platform) {
      case 'facebook':
        return <FaFacebook size={20} />;
      case 'twitter':
        return <FaTwitter size={20} />;
      case 'instagram':
        return <FaInstagram size={20} />;
      case 'linkedin':
        return <FaLinkedin size={20} />;
      default:
        return null;
    }
  };

  // Format copyright text with current year
  const formatCopyright = (text) => {
    return text.replace('{year}', new Date().getFullYear());
  };

  return (
    <footer className="bg-blue-900 text-white">
      <div className="container mx-auto px-4 py-12">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
          {/* Company Info */}
          <div className="space-y-4">
            <h3 className="text-white text-2xl font-bold">{footerData.companyInfo.name}</h3>
            <p className="text-white">
              {footerData.companyInfo.description}
            </p>
            <div className="flex space-x-4">
              {footerData.companyInfo.socialLinks.map((social, index) => (
                <a
                  key={index}
                  href={social.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-white hover:text-blue-300 transition-colors"
                  aria-label={social.platform}
                >
                  {renderSocialIcon(social.platform)}
                </a>
              ))}
            </div>
          </div>

          {/* Quick Links */}
          <div>
            <h4 className="text-lg font-semibold mb-4 text-white">Quick Links</h4>
            <ul className="space-y-2">
              {footerData.quickLinks.map((link, index) => (
                <li key={index}>
                  <Link to={link.url} className="text-white hover:text-blue-300 transition-colors">
                    {link.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>

          {/* Services */}
          <div>
            <h4 className="text-lg font-semibold mb-4 text-white">Our Services</h4>
            <ul className="space-y-2">
              {footerData.services.map((service, index) => (
                <li key={index}>
                  <Link to={service.url} className="text-white hover:text-blue-300 transition-colors">
                    {service.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>

          {/* Contact Info */}
          <div>
            <h4 className="text-lg font-semibold mb-4 text-white">Contact Us</h4>
            <ul className="space-y-4">
              <li className="flex items-start space-x-3">
                <FaMapMarkerAlt className="mt-1 text-white" />
                <span className="text-white">
                  {footerData.contactInfo.address}
                </span>
              </li>
              <li className="flex items-center space-x-3">
                <FaPhone className="text-white" />
                <a href={`tel:${footerData.contactInfo.phone.replace(/[^0-9+]/g, '')}`} className="text-white hover:text-blue-300 transition-colors">
                  {footerData.contactInfo.phone}
                </a>
              </li>
              <li className="flex items-center space-x-3">
                <FaEnvelope className="text-white" />
                <a href={`mailto:${footerData.contactInfo.email}`} className="text-white hover:text-blue-300 transition-colors">
                  {footerData.contactInfo.email}
                </a>
              </li>
            </ul>
          </div>
        </div>


        {/* Copyright */}
        <div className="border-t border-blue-800 mt-8 pt-8 text-center text-white">
          <p>
            {formatCopyright(footerData.copyright)}
            <br />
            <span className="text-sm text-blue-200">YOUR DREAM OUR JOB!</span>
          </p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
