import React, { useState, useEffect } from 'react';
import Services from '../components/services/Services';
import { FaGraduationCap, FaPassport, FaUniversity, FaLanguage, FaCheck, FaSpinner } from 'react-icons/fa';

// Map icon names to actual icon components
const iconMap = {
  FaGraduationCap: <FaGraduationCap className="w-12 h-12 text-navy" />,
  FaPassport: <FaPassport className="w-12 h-12 text-navy" />,
  FaUniversity: <FaUniversity className="w-12 h-12 text-navy" />,
  FaLanguage: <FaLanguage className="w-12 h-12 text-navy" />
};

// Default services data if nothing is in localStorage
const defaultServices = [
  {
    _id: 'service-1',
    id: "study-abroad",
    iconName: "FaGraduationCap",
    title: "Study Abroad Consultation",
    description: "Expert guidance on choosing the right country, university, and program for your academic goals.",
    features: [
      "Country and university selection",
      "Program matching based on your profile",
      "Scholarship and funding guidance",
      "Cultural adaptation support"
    ],
    order: 1
  },
  {
    _id: 'service-2',
    id: "visa",
    iconName: "FaPassport",
    title: "Visa Application Guidance",
    description: "Comprehensive support for visa applications, documentation, and interview preparation.",
    features: [
      "Document checklist and preparation",
      "Application form assistance",
      "Interview preparation",
      "Post-approval guidance"
    ],
    order: 2
  },
  {
    _id: 'service-3',
    id: "admission",
    iconName: "FaUniversity",
    title: "University Admission Support",
    description: "Personalized assistance with university applications, essays, and admission requirements.",
    features: [
      "Application strategy development",
      "Personal statement review",
      "Recommendation letter guidance",
      "Portfolio preparation"
    ],
    order: 3
  },
  {
    _id: 'service-4',
    id: "language",
    iconName: "FaLanguage",
    title: "Language Training",
    description: "Specialized language courses to help you meet the language requirements of your chosen program.",
    features: [
      "IELTS/TOEFL preparation",
      "Academic writing skills",
      "Speaking and listening practice",
      "Test-taking strategies"
    ],
    order: 4
  }
];

const ServicesPage = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Fetch services data from localStorage
  useEffect(() => {
    const fetchServicesPageData = () => {
      try {
        setLoading(true);
        // Get services page data from localStorage
        const storedServicesPage = JSON.parse(localStorage.getItem('servicesPageDatabase'));

        if (storedServicesPage) {
          setServices(storedServicesPage.services || []);
        } else {
          // Use default data if nothing is in localStorage
          setServices(defaultServices);
        }
        setLoading(false);
      } catch (err) {
        console.error('Error fetching services page data:', err);
        setError('Failed to load services page data');
        setServices(defaultServices);
        setLoading(false);
      }
    };

    // Initial fetch
    fetchServicesPageData();

    // Set up storage event listener to detect changes from other tabs/windows
    const handleStorageChange = (e) => {
      if (e.key === 'servicesPageDatabase') {
        console.log('Services page database updated, refreshing...');
        fetchServicesPageData();
      }
    };

    window.addEventListener('storage', handleStorageChange);

    // Poll for changes every 2 seconds (as a backup for changes in the same tab)
    const intervalId = setInterval(fetchServicesPageData, 2000);

    // Cleanup
    return () => {
      window.removeEventListener('storage', handleStorageChange);
      clearInterval(intervalId);
    };
  }, []);
  // Sort services by order property
  const sortedServices = [...services].sort((a, b) => (a.order || 0) - (b.order || 0));

  // Get hero section data from localStorage
  const [heroData, setHeroData] = useState({
    title: "Our Services",
    description: "Comprehensive support for your educational journey, from initial consultation to successful admission."
  });

  // Fetch hero data
  useEffect(() => {
    const fetchHeroData = () => {
      try {
        const storedServicesPage = JSON.parse(localStorage.getItem('servicesPageDatabase'));
        if (storedServicesPage && storedServicesPage.hero) {
          setHeroData(storedServicesPage.hero);
        }
      } catch (err) {
        console.error('Error fetching hero data:', err);
      }
    };

    fetchHeroData();

    // Listen for changes
    const handleStorageChange = (e) => {
      if (e.key === 'servicesPageDatabase') {
        fetchHeroData();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Get process section data from localStorage
  const [processData, setProcessData] = useState({
    title: "Our Process",
    description: "We follow a structured approach to ensure your success at every step.",
    steps: [
      { number: 1, title: "Initial Consultation", description: "Understanding your goals and requirements" },
      { number: 2, title: "Profile Assessment", description: "Evaluating your academic background and preferences" },
      { number: 3, title: "Application Support", description: "Guiding you through the application process" },
      { number: 4, title: "Post-Admission", description: "Supporting your transition to university life" }
    ]
  });

  // Fetch process data
  useEffect(() => {
    const fetchProcessData = () => {
      try {
        const storedServicesPage = JSON.parse(localStorage.getItem('servicesPageDatabase'));
        if (storedServicesPage && storedServicesPage.process) {
          setProcessData(storedServicesPage.process);
        }
      } catch (err) {
        console.error('Error fetching process data:', err);
      }
    };

    fetchProcessData();

    // Listen for changes
    const handleStorageChange = (e) => {
      if (e.key === 'servicesPageDatabase') {
        fetchProcessData();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Get CTA section data from localStorage
  const [ctaData, setCtaData] = useState({
    title: "Ready to Start Your Journey?",
    description: "Contact us today to learn more about our services and how we can help you achieve your academic goals.",
    buttonText: "Get in Touch",
    buttonLink: "/contact"
  });

  // Fetch CTA data
  useEffect(() => {
    const fetchCtaData = () => {
      try {
        const storedServicesPage = JSON.parse(localStorage.getItem('servicesPageDatabase'));
        if (storedServicesPage && storedServicesPage.cta) {
          setCtaData(storedServicesPage.cta);
        }
      } catch (err) {
        console.error('Error fetching CTA data:', err);
      }
    };

    fetchCtaData();

    // Listen for changes
    const handleStorageChange = (e) => {
      if (e.key === 'servicesPageDatabase') {
        fetchCtaData();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  if (loading) {
    return (
      <div className="pt-16 flex justify-center items-center min-h-screen">
        <FaSpinner className="animate-spin text-navy w-12 h-12" />
      </div>
    );
  }

  if (error) {
    return (
      <div className="pt-16 flex justify-center items-center min-h-screen">
        <div className="text-center">
          <p className="text-red-500 text-xl">{error}</p>
          <p className="mt-4">Please try refreshing the page.</p>
        </div>
      </div>
    );
  }

  return (
    <div className="pt-16">
      {/* Hero Section */}
      <section className="relative py-20">
        <div className="absolute inset-0 bg-navy/90"></div>
        <div className="container mx-auto px-4 relative z-10">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-6">
            {heroData.title}
          </h1>
          <p className="text-xl text-gray-200 max-w-3xl">
            {heroData.description}
          </p>
        </div>
      </section>

      {/* Services Grid */}
      <section className="py-20">
        <div className="container mx-auto px-4">
          {sortedServices.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-gray-600">No services available at the moment.</p>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 gap-12">
              {sortedServices.map((service) => (
                <div
                  key={service._id}
                  className="bg-white p-8 rounded-lg shadow-md hover:shadow-lg transition-shadow"
                >
                  <div className="mb-6">{iconMap[service.iconName] || <FaGraduationCap className="w-12 h-12 text-navy" />}</div>
                  <h2 className="text-2xl font-bold text-navy mb-4">
                    {service.title}
                  </h2>
                  <p className="text-gray-600 mb-6">
                    {service.description}
                  </p>
                  <ul className="space-y-3">
                    {service.features.map((feature, featureIndex) => (
                      <li key={featureIndex} className="flex items-start">
                        <FaCheck className="text-green-500 mt-1 mr-3 flex-shrink-0" />
                        <span className="text-gray-600">{feature}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* Process Section */}
      <section className="py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <div className="text-center mb-16">
            <h2 className="text-3xl md:text-4xl font-bold text-navy mb-4">
              {processData.title}
            </h2>
            <p className="text-gray-600 max-w-2xl mx-auto">
              {processData.description}
            </p>
          </div>

          <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
            {processData.steps.map((step) => (
              <div key={step.number} className="text-center">
                <div className="w-16 h-16 bg-navy text-white rounded-full flex items-center justify-center mx-auto mb-4">
                  {step.number}
                </div>
                <h3 className="text-xl font-semibold text-navy mb-2">{step.title}</h3>
                <p className="text-gray-600">{step.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-navy text-white">
        <div className="container mx-auto px-4 text-center">
          <h2 className="text-3xl md:text-4xl font-bold mb-6">
            {ctaData.title}
          </h2>
          <p className="text-xl text-gray-200 mb-8 max-w-2xl mx-auto">
            {ctaData.description}
          </p>
          <a
            href={ctaData.buttonLink}
            className="inline-block px-8 py-4 bg-white text-navy font-semibold rounded-md hover:bg-gray-100 transition-colors"
          >
            {ctaData.buttonText}
          </a>
        </div>
      </section>
    </div>
  );
};

export default ServicesPage;
