import React, { useState, useEffect } from 'react';
import { getContentByPage } from '../../services/contentService';
import { toast } from 'react-toastify';
import LoadingSpinner from '../shared/LoadingSpinner';
import ErrorMessage from '../shared/ErrorMessage';

const CountriesWeOffer = () => {
  const [content, setContent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchContent = async () => {
      try {
        setLoading(true);
        const data = await getContentByPage('countries');
        setContent(data);
      } catch (err) {
        setError('Failed to fetch countries content');
        toast.error('Failed to fetch countries content');
      } finally {
        setLoading(false);
      }
    };

    fetchContent();
  }, []);

  if (loading) return <LoadingSpinner />;
  if (error) return <ErrorMessage message={error} />;
  if (!content) return null;

  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <div className="max-w-3xl mx-auto text-center mb-12">
          <h2 className="text-3xl md:text-4xl font-bold mb-6 text-navy">
            {content.title}
          </h2>
          <p className="text-lg text-gray-600">
            {content.description}
          </p>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {content.countries.map((country, index) => (
            <div
              key={index}
              className="bg-white p-6 rounded-lg shadow-md hover:shadow-lg transition-shadow duration-300"
            >
              <div className="flex items-center mb-4">
                <img
                  src={country.flag}
                  alt={`${country.name} flag`}
                  className="w-8 h-8 rounded-full mr-3"
                />
                <h3 className="text-xl font-semibold text-navy">
                  {country.name}
                </h3>
              </div>
              <p className="text-gray-600 mb-4">
                {country.description}
              </p>
              <ul className="space-y-2">
                {country.features.map((feature, featureIndex) => (
                  <li key={featureIndex} className="flex items-center text-gray-600">
                    <span className="text-navy mr-2">âœ“</span>
                    {feature}
                  </li>
                ))}
              </ul>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default CountriesWeOffer;
