import React, { useEffect, useState } from 'react';
import { Routes, Route } from 'react-router-dom';
import { Layout } from './components/layout';
import { ROUTES } from './constants/routes';
import { motion, AnimatePresence } from 'framer-motion';

// Page imports
import Home from './pages/Home';
import ContactPage from './pages/Contact';
import AdmissionProcessPage from './pages/AdmissionProcess';
import CVJobsPortalPage from './pages/CVJobsPortal';

// Preloader Component
const Preloader = () => (
  <motion.div
    initial={{ opacity: 1 }}
    exit={{ opacity: 0 }}
    className="fixed inset-0 z-50 flex items-center justify-center bg-white"
  >
    <div className="text-center">
      <motion.div
        animate={{ 
          scale: [1, 1.2, 1],
          rotate: [0, 360]
        }}
        transition={{
          duration: 2,
          repeat: Infinity,
          ease: "easeInOut"
        }}
        className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full"
      />
      <p className="mt-4 text-lg font-medium text-gray-600">Loading...</p>
    </div>
  </motion.div>
);

function App() {
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Simulate loading time
    const timer = setTimeout(() => {
      setLoading(false);
    }, 2000);

    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      <AnimatePresence>
        {loading && <Preloader />}
      </AnimatePresence>
      <Routes>
        <Route path="/" element={<Layout />}>
          <Route index element={<Home />} />
          <Route path={ROUTES.ADMISSION_PROCESS} element={<AdmissionProcessPage />} />
          <Route path={ROUTES.CV_JOBS} element={<CVJobsPortalPage />} />
          <Route path={ROUTES.CONTACT} element={<ContactPage />} />
        </Route>
      </Routes>
    </>
  );
}

export default App;
