import React, { useEffect, useState } from 'react';
import { toast } from 'react-toastify';
import { ServicesService } from './AdminServices';
import LoadingSpinner from '../../components/shared/LoadingSpinner';
import ErrorMessage from '../../components/shared/ErrorMessage';

const ServicesManagement = () => {
  const [services, setServices] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedService, setSelectedService] = useState(null);
  const [formData, setFormData] = useState({
    title: '',
    description: '',
    icon: '',
    featured: false
  });
  const [isEditing, setIsEditing] = useState(false);

  // Load all services on component mount
  useEffect(() => {
    const loadServices = async () => {
      try {
        setLoading(true);
        const data = await ServicesService.getAllServices();
        setServices(data);
        setLoading(false);
      } catch (err) {
        setError(err.message || 'Failed to load services');
        setLoading(false);
      }
    };

    loadServices();
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      setLoading(true);
      if (isEditing && selectedService) {
        // Update existing service
        const updatedService = await ServicesService.updateService(selectedService._id, formData);
        setServices(services.map(service => 
          service._id === updatedService._id ? updatedService : service
        ));
        setIsEditing(false);
        setSelectedService(null);
      } else {
        // Create new service
        const newService = await ServicesService.createService(formData);
        setServices([...services, newService]);
      }
      // Reset form
      setFormData({
        title: '',
        description: '',
        icon: '',
        featured: false
      });
      setLoading(false);
    } catch (error) {
      toast.error(error.message || 'Something went wrong');
      setLoading(false);
    }
  };

  // Handle edit service
  const handleEdit = (service) => {
    setSelectedService(service);
    setFormData({
      title: service.title,
      description: service.description,
      icon: service.icon,
      featured: service.featured
    });
    setIsEditing(true);
  };

  // Handle delete service
  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this service?')) {
      try {
        setLoading(true);
        await ServicesService.deleteService(id);
        setServices(services.filter(service => service._id !== id));
        setLoading(false);
      } catch (error) {
        toast.error(error.message || 'Failed to delete service');
        setLoading(false);
      }
    }
  };

  // Cancel editing
  const handleCancel = () => {
    setIsEditing(false);
    setSelectedService(null);
    setFormData({
      title: '',
      description: '',
      icon: '',
      featured: false
    });
  };

  // List of common icons
  const iconOptions = [
    'graduation-cap',
    'passport',
    'briefcase',
    'globe',
    'book',
    'university',
    'plane',
    'chart-line',
    'laptop',
    'users'
  ];

  if (loading && services.length === 0) {
    return <LoadingSpinner />;
  }

  if (error) {
    return <ErrorMessage message={error} />;
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Services Management</h1>

      {/* Service Form */}
      <div className="bg-white shadow-md rounded-lg p-6 mb-8">
        <h2 className="text-xl font-semibold mb-4">
          {isEditing ? 'Edit Service' : 'Add New Service'}
        </h2>
        <form onSubmit={handleSubmit}>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="title">
                Service Title
              </label>
              <input
                type="text"
                id="title"
                name="title"
                value={formData.title}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              />
            </div>
            <div className="mb-4">
              <label className="block text-gray-700 mb-2" htmlFor="icon">
                Icon
              </label>
              <select
                id="icon"
                name="icon"
                value={formData.icon}
                onChange={handleChange}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              >
                <option value="">Select an icon</option>
                {iconOptions.map((icon) => (
                  <option key={icon} value={icon}>
                    {icon}
                  </option>
                ))}
              </select>
            </div>
            <div className="mb-4 md:col-span-2">
              <label className="block text-gray-700 mb-2" htmlFor="description">
                Description
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows="4"
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                required
              ></textarea>
            </div>
            <div className="mb-4 flex items-center">
              <input
                type="checkbox"
                id="featured"
                name="featured"
                checked={formData.featured}
                onChange={handleChange}
                className="mr-2"
              />
              <label className="text-gray-700" htmlFor="featured">
                Featured Service
              </label>
            </div>
          </div>
          <div className="flex justify-end mt-4">
            {isEditing && (
              <button
                type="button"
                onClick={handleCancel}
                className="px-4 py-2 bg-gray-300 text-gray-700 rounded-md mr-2 hover:bg-gray-400"
              >
                Cancel
              </button>
            )}
            <button
              type="submit"
              className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
              disabled={loading}
            >
              {loading ? 'Saving...' : isEditing ? 'Update Service' : 'Add Service'}
            </button>
          </div>
        </form>
      </div>

      {/* Services List */}
      <div className="bg-white shadow-md rounded-lg p-6">
        <h2 className="text-xl font-semibold mb-4">All Services</h2>
        {services.length === 0 ? (
          <p className="text-gray-500">No services found.</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full bg-white">
              <thead>
                <tr className="bg-gray-100">
                  <th className="py-2 px-4 text-left">Title</th>
                  <th className="py-2 px-4 text-left">Icon</th>
                  <th className="py-2 px-4 text-left">Featured</th>
                  <th className="py-2 px-4 text-left">Actions</th>
                </tr>
              </thead>
              <tbody>
                {services.map((service) => (
                  <tr key={service._id} className="border-t">
                    <td className="py-2 px-4">{service.title}</td>
                    <td className="py-2 px-4">{service.icon}</td>
                    <td className="py-2 px-4">
                      {service.featured ? 'Yes' : 'No'}
                    </td>
                    <td className="py-2 px-4">
                      <button
                        onClick={() => handleEdit(service)}
                        className="text-blue-500 hover:text-blue-700 mr-2"
                      >
                        Edit
                      </button>
                      <button
                        onClick={() => handleDelete(service._id)}
                        className="text-red-500 hover:text-red-700"
                      >
                        Delete
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default ServicesManagement;
