import React, { useState, useEffect } from 'react';
import { FaFacebook, FaTwitter, FaInstagram, FaLinkedin, FaEnvelope, FaPhone, FaMapMarkerAlt } from 'react-icons/fa';
import { Link } from 'react-router-dom';
import { ROUTES } from '../../constants/routes';

const Footer = () => {
  const [footerData, setFooterData] = useState({
    companyInfo: {
      name: 'GradWay',
      description: 'Your trusted partner in academic success. We provide expert guidance and support for your educational journey.',
      socialLinks: [
        { platform: 'facebook', url: 'https://facebook.com' },
        { platform: 'twitter', url: 'https://twitter.com' },
        { platform: 'instagram', url: 'https://instagram.com' },
        { platform: 'linkedin', url: 'https://linkedin.com' }
      ]
    },
    quickLinks: [
      { name: 'Home', url: '/' },
      { name: 'About Us', url: '/about' },
      { name: 'Services', url: '/services' },
      { name: 'Blog', url: '/blog' },
      { name: 'Contact', url: '/contact' }
    ],
    services: [
      { name: 'Study Abroad Consultation', url: '/services#study-abroad' },
      { name: 'Visa Application Guidance', url: '/services#visa' },
      { name: 'University Admission Support', url: '/services#admission' },
      { name: 'Language Training', url: '/services#language' }
    ],
    contactInfo: {
      address: '123 Education Street, Academic City, AC 12345',
      phone: '+1 (234) 567-890',
      email: 'info@gradway.com'
    },
    copyright: '© {year} GradWay. All rights reserved.'
  });

  useEffect(() => {
    // Fetch footer data from localStorage
    const fetchFooterData = () => {
      try {
        const data = JSON.parse(localStorage.getItem('footerDatabase'));
        if (data) {
          setFooterData(data);
        }
      } catch (error) {
        console.error('Error fetching footer data:', error);
      }
    };

    fetchFooterData();

    // Set up event listener for storage changes
    const handleStorageChange = (e) => {
      if (e.key === 'footerDatabase') {
        fetchFooterData();
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  // Helper function to render social media icons
  const renderSocialIcon = (platform, url) => {
    switch (platform) {
      case 'facebook':
        return <FaFacebook size={20} />;
      case 'twitter':
        return <FaTwitter size={20} />;
      case 'instagram':
        return <FaInstagram size={20} />;
      case 'linkedin':
        return <FaLinkedin size={20} />;
      default:
        return null;
    }
  };

  // Format copyright text with current year
  const formatCopyright = (text) => {
    return text.replace('{year}', new Date().getFullYear());
  };

  return (
    <footer className="bg-navy text-white">
      <div className="container mx-auto px-4 py-12">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-8">
          {/* Company Info */}
          <div className="space-y-4">
            <h3 className="text-white text-2xl font-bold">{footerData.companyInfo.name}</h3>
            <p className="text-white">
              {footerData.companyInfo.description}
            </p>
            <div className="flex space-x-4">
              {footerData.companyInfo.socialLinks.map((social, index) => (
                <a
                  key={index}
                  href={social.url}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="text-white hover:text-gray-300 transition-colors"
                  aria-label={social.platform}
                >
                  {renderSocialIcon(social.platform, social.url)}
                </a>
              ))}
            </div>
          </div>

          {/* Quick Links */}
          <div>
            <h4 className="text-lg font-semibold mb-4 text-white">Quick Links</h4>
            <ul className="space-y-2">
              {footerData.quickLinks.map((link, index) => (
                <li key={index}>
                  <Link to={link.url} className="text-white hover:text-gray-300 transition-colors">
                    {link.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>

          {/* Services */}
          <div>
            <h4 className="text-lg font-semibold mb-4 text-white">Our Services</h4>
            <ul className="space-y-2">
              {footerData.services.map((service, index) => (
                <li key={index}>
                  <Link to={service.url} className="text-white hover:text-gray-300 transition-colors">
                    {service.name}
                  </Link>
                </li>
              ))}
            </ul>
          </div>

          {/* Contact Info */}
          <div>
            <h4 className="text-lg font-semibold mb-4 text-white">Contact Us</h4>
            <ul className="space-y-4">
              <li className="flex items-start space-x-3">
                <FaMapMarkerAlt className="mt-1 text-white" />
                <span className="text-white">
                  {footerData.contactInfo.address}
                </span>
              </li>
              <li className="flex items-center space-x-3">
                <FaPhone className="text-white" />
                <a href={`tel:${footerData.contactInfo.phone.replace(/[^0-9+]/g, '')}`} className="text-white hover:text-gray-300 transition-colors">
                  {footerData.contactInfo.phone}
                </a>
              </li>
              <li className="flex items-center space-x-3">
                <FaEnvelope className="text-white" />
                <a href={`mailto:${footerData.contactInfo.email}`} className="text-white hover:text-gray-300 transition-colors">
                  {footerData.contactInfo.email}
                </a>
              </li>
            </ul>
          </div>
        </div>

        {/* Copyright */}
        <div className="border-t border-gray-700 mt-8 pt-8 text-center text-white">
          <p>{formatCopyright(footerData.copyright)}</p>
        </div>
      </div>
    </footer>
  );
};

export default Footer;
