import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { FaUpload, FaFileAlt, FaSearch, FaBriefcase } from 'react-icons/fa';

const CVJobsPortal = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    education: '',
    experience: '',
    cvFile: null,
  });
  
  const [submitting, setSubmitting] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };
  
  const handleFileChange = (e) => {
    setFormData({ ...formData, cvFile: e.target.files[0] });
  };
  
  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitting(true);
    
    // Simulate form submission
    setTimeout(() => {
      setSubmitting(false);
      setSubmitted(true);
      setFormData({
        name: '',
        email: '',
        phone: '',
        education: '',
        experience: '',
        cvFile: null,
      });
      
      // Reset the file input
      const fileInput = document.getElementById('cvFile');
      if (fileInput) fileInput.value = '';
      
      // Reset submitted status after 5 seconds
      setTimeout(() => {
        setSubmitted(false);
      }, 5000);
    }, 1500);
  };
  
  const resources = [
    {
      id: 1,
      title: 'CV Writing Tips',
      description: 'Learn how to create a standout CV that gets noticed by employers',
      icon: <FaFileAlt className="text-blue-600" />,
    },
    {
      id: 2,
      title: 'Job Search Strategies',
      description: 'Effective methods to find and apply for jobs in your field',
      icon: <FaSearch className="text-blue-600" />,
    },
    {
      id: 3,
      title: 'Interview Preparation',
      description: 'Tips and techniques to succeed in job interviews',
      icon: <FaBriefcase className="text-blue-600" />,
    },
  ];

  return (
    <section className="py-20 bg-gray-50">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          viewport={{ once: true }}
          className="text-center mb-16"
        >
          <h2 className="text-3xl md:text-4xl font-bold text-blue-600 mb-4">CV & Jobs Portal</h2>
          <p className="text-gray-600 max-w-3xl mx-auto">
            Submit your CV to our database, track job applications, and access valuable employability resources.
            Our dedicated team will help you connect with the right opportunities.
          </p>
        </motion.div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-12">
          {/* CV Submission Form */}
          <motion.div
            initial={{ opacity: 0, x: -20 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
            className="bg-white rounded-lg shadow-lg p-8"
          >
            <h3 className="text-2xl font-bold text-gray-800 mb-6">Submit Your CV</h3>
            
            {submitted ? (
              <div className="bg-green-100 text-green-700 p-4 rounded-lg mb-6">
                Thank you for submitting your CV! Our team will review it and contact you soon.
              </div>
            ) : null}
            
            <form onSubmit={handleSubmit}>
              <div className="mb-4">
                <label htmlFor="name" className="block text-gray-700 font-medium mb-2">Full Name*</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              
              <div className="mb-4">
                <label htmlFor="email" className="block text-gray-700 font-medium mb-2">Email Address*</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              
              <div className="mb-4">
                <label htmlFor="phone" className="block text-gray-700 font-medium mb-2">Phone Number*</label>
                <input
                  type="tel"
                  id="phone"
                  name="phone"
                  value={formData.phone}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  required
                />
              </div>
              
              <div className="mb-4">
                <label htmlFor="education" className="block text-gray-700 font-medium mb-2">Highest Education</label>
                <input
                  type="text"
                  id="education"
                  name="education"
                  value={formData.education}
                  onChange={handleChange}
                  className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                />
              </div>
              
              <div className="mb-4">
                <label htmlFor="experience" className="block text-gray-700 font-medium mb-2">Work Experience</label>
                <textarea
                  id="experience"
                  name="experience"
                  value={formData.experience}
                  onChange={handleChange}
                  rows="3"
                  className="w-full px-4 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                ></textarea>
              </div>
              
              <div className="mb-6">
                <label htmlFor="cvFile" className="block text-gray-700 font-medium mb-2">Upload CV (PDF or DOC)*</label>
                <div className="border border-dashed border-gray-300 rounded-md p-4 text-center">
                  <FaUpload className="mx-auto text-gray-400 mb-2" />
                  <p className="text-sm text-gray-500 mb-2">Click to browse or drag and drop</p>
                  <input
                    type="file"
                    id="cvFile"
                    onChange={handleFileChange}
                    className="w-full opacity-0 absolute inset-0 cursor-pointer"
                    accept=".pdf,.doc,.docx"
                    required
                  />
                  {formData.cvFile && (
                    <p className="text-sm text-blue-600">{formData.cvFile.name}</p>
                  )}
                </div>
              </div>
              
              <button
                type="submit"
                disabled={submitting}
                className={`w-full px-6 py-3 bg-blue-600 text-white font-semibold rounded-md transition-colors ${
                  submitting ? 'opacity-70 cursor-not-allowed' : 'hover:bg-blue-700'
                }`}
              >
                {submitting ? 'Submitting...' : 'Submit CV'}
              </button>
            </form>
          </motion.div>

          {/* Employability Resources */}
          <motion.div
            initial={{ opacity: 0, x: 20 }}
            whileInView={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.5 }}
            viewport={{ once: true }}
            className="bg-white rounded-lg shadow-lg p-8"
          >
            <h3 className="text-2xl font-bold text-gray-800 mb-6">Employability Resources</h3>
            
            <div className="space-y-6">
              {resources.map((resource, index) => (
                <motion.div
                  key={resource.id}
                  initial={{ opacity: 0, y: 10 }}
                  whileInView={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.1 * index, duration: 0.4 }}
                  viewport={{ once: true }}
                  className="border-b border-gray-200 pb-6 last:border-b-0 last:pb-0"
                >
                  <div className="flex items-start">
                    <div className="mr-4 mt-1">{resource.icon}</div>
                    <div>
                      <h4 className="text-lg font-semibold text-gray-800 mb-2">{resource.title}</h4>
                      <p className="text-gray-600">{resource.description}</p>
                      <button className="mt-2 text-blue-600 font-medium hover:text-blue-800 transition-colors">
                        Learn More
                      </button>
                    </div>
                  </div>
                </motion.div>
              ))}
            </div>
            
            <div className="mt-8 bg-blue-50 p-4 rounded-lg">
              <h4 className="text-blue-800 font-semibold mb-2">Job Application Tracking</h4>
              <p className="text-gray-700 text-sm">
                Register or login to track your job applications, receive notifications about new positions, 
                and get personalized job recommendations based on your profile.
              </p>
              <button className="mt-3 px-4 py-2 bg-blue-600 text-white font-medium rounded-md hover:bg-blue-700 transition-colors">
                Register Now
              </button>
            </div>
          </motion.div>
        </div>
      </div>
    </section>
  );
};

export default CVJobsPortal;
