import React, { useState, useEffect } from 'react';
import { Routes, Route, Link, useNavigate, useLocation } from 'react-router-dom';
import { FaGraduationCap, FaHandshake, FaComments, FaEnvelope, FaSignOutAlt, FaHome, FaUsers, FaBlog, FaCog, FaEdit, 
  FaInfoCircle, FaGlobeAmericas, FaTools, FaPhone, FaBars, FaTimes, FaSearch, FaBell, FaUserCircle, FaChevronDown } from 'react-icons/fa';
import Overview from './Overview';
import ContentManagement from './ContentManagement';
import CoursesManagement from './CoursesManagement';
import ServicesManagement from './ServicesManagement';
import TestimonialsManagement from './TestimonialsManagement';
import HeroSectionManagement from './HeroSectionManagement';
import AboutSectionManagement from './AboutSectionManagement';
import CountriesManagement from './CountriesManagement';
import ServicesPageManagement from './ServicesPageManagement';
import AboutPageManagement from './AboutPageManagement';
import ContactPageManagement from './ContactPageManagement';

const Dashboard = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [notificationsOpen, setNotificationsOpen] = useState(false);
  const [stats, setStats] = useState({
    courses: 0,
    services: 0,
    testimonials: 0,
    contacts: 0
  });
  const navigate = useNavigate();
  const location = useLocation();

  useEffect(() => {
    fetchDashboardStats();
    // Close mobile sidebar when route changes
    setIsSidebarOpen(window.innerWidth >= 768);
  }, [location.pathname]);

  const fetchDashboardStats = async () => {
    try {
      // For demo purposes, we'll use mock data
      // In a real app, you would fetch this from your API
      setStats({
        courses: 12,
        services: 8,
        testimonials: 24,
        contacts: 18
      });

      // Uncomment this for real API integration
      /*
      const token = localStorage.getItem('token');
      const response = await fetch('http://localhost:5000/api/admin/stats', {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const data = await response.json();
      if (data.success) {
        setStats(data.stats);
      }
      */
    } catch (error) {
      console.error('Error fetching dashboard stats:', error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/admin/login');
  };

  // Group menu items by category
  const menuCategories = [
    {
      name: 'Dashboard',
      items: [
        { icon: <FaHome className="w-5 h-5" />, text: 'Overview', link: '/admin/dashboard' },
      ]
    },
    {
      name: 'Content',
      items: [
        { icon: <FaEdit className="w-5 h-5" />, text: 'Content Management', link: '/admin/dashboard/content' },
        { icon: <FaCog className="w-5 h-5" />, text: 'Hero Section', link: '/admin/dashboard/hero' },
        { icon: <FaInfoCircle className="w-5 h-5" />, text: 'About Section', link: '/admin/dashboard/about' },
      ]
    },
    {
      name: 'Pages',
      items: [
        { icon: <FaTools className="w-5 h-5" />, text: 'Services Page', link: '/admin/dashboard/services-page' },
        { icon: <FaInfoCircle className="w-5 h-5" />, text: 'About Us Page', link: '/admin/dashboard/about-page' },
        { icon: <FaPhone className="w-5 h-5" />, text: 'Contact Page', link: '/admin/dashboard/contact-page' },
      ]
    },
    {
      name: 'Management',
      items: [
        { icon: <FaGraduationCap className="w-5 h-5" />, text: 'Courses', link: '/admin/dashboard/courses' },
        { icon: <FaHandshake className="w-5 h-5" />, text: 'Services', link: '/admin/dashboard/services' },
        { icon: <FaComments className="w-5 h-5" />, text: 'Testimonials', link: '/admin/dashboard/testimonials' },
        { icon: <FaEnvelope className="w-5 h-5" />, text: 'Contact Requests', link: '/admin/dashboard/contacts' },
        { icon: <FaBlog className="w-5 h-5" />, text: 'Blog Posts', link: '/admin/dashboard/blog' },
        { icon: <FaUsers className="w-5 h-5" />, text: 'Team Members', link: '/admin/dashboard/team' },
        { icon: <FaGlobeAmericas className="w-5 h-5" />, text: 'Countries', link: '/admin/dashboard/countries' },
      ]
    }
  ];

  // Get current page title
  const getCurrentPageTitle = () => {
    const path = location.pathname.split('/').filter(Boolean);
    if (path.length <= 2) return 'Dashboard Overview';

    // Convert path to title (e.g., 'contact-page' -> 'Contact Page')
    const pageName = path[path.length - 1];
    return pageName
      .split('-')
      .map(word => word.charAt(0).toUpperCase() + word.slice(1))
      .join(' ');
  };

  // Mock notifications
  const notifications = [
    { id: 1, text: 'New contact request received', time: '5 minutes ago', read: false },
    { id: 2, text: 'Course update completed', time: '1 hour ago', read: false },
    { id: 3, text: 'New testimonial submitted', time: '3 hours ago', read: true },
  ];

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div
        className={`bg-gradient-to-b from-navy to-navy-dark text-white w-64 space-y-6 py-4 px-2 absolute inset-y-0 left-0 transform ${
          isSidebarOpen ? 'translate-x-0' : '-translate-x-full'
        } md:relative md:translate-x-0 transition duration-200 ease-in-out z-30 shadow-xl`}
      >
        <div className="flex items-center justify-between px-4 py-2 border-b border-navy-light mb-4">
          <Link to="/admin/dashboard" className="flex items-center space-x-2">
            <span className="text-xl font-extrabold bg-clip-text text-transparent bg-gradient-to-r from-white to-blue-200">GradWay Admin</span>
          </Link>
          <button
            className="md:hidden text-white hover:text-gray-300"
            onClick={() => setIsSidebarOpen(!isSidebarOpen)}
          >
            <FaTimes className="h-6 w-6" />
          </button>
        </div>

        <div className="px-4 py-2 mb-6">
          <div className="flex items-center space-x-3 bg-navy-light/40 p-3 rounded-lg">
            <div className="bg-navy-light rounded-full p-2">
              <FaUserCircle className="w-6 h-6 text-white" />
            </div>
            <div>
              <p className="text-sm font-medium text-white">Admin User</p>
              <p className="text-xs text-gray-300">Administrator</p>
            </div>
          </div>
        </div>

        <nav className="flex flex-col space-y-1 px-2">
          {menuCategories.map((category) => (
            <div key={category.name} className="mb-4">
              <h3 className="text-xs uppercase tracking-wider text-gray-400 font-semibold px-4 mb-2">{category.name}</h3>
              {category.items.map((item) => {
                const isActive = location.pathname === item.link || 
                                (item.link !== '/admin/dashboard' && location.pathname.startsWith(item.link));
                return (
                  <Link
                    key={item.text}
                    to={item.link}
                    className={`flex items-center space-x-2 py-2.5 px-4 rounded-lg transition duration-200 
                      ${isActive ? 'bg-navy-light text-white font-medium' : 'text-gray-300 hover:bg-navy-light/50 hover:text-white'}`}
                  >
                    <span className={`${isActive ? 'text-blue-300' : 'text-gray-400'}`}>{item.icon}</span>
                    <span>{item.text}</span>
                    {isActive && <div className="w-1.5 h-1.5 rounded-full bg-blue-400 ml-auto"></div>}
                  </Link>
                );
              })}
            </div>
          ))}
          <div className="pt-4 mt-4 border-t border-navy-light">
            <button
              onClick={handleLogout}
              className="flex items-center space-x-2 py-2.5 px-4 rounded-lg transition duration-200 w-full text-left text-red-300 hover:bg-navy-light/50 hover:text-red-200"
            >
              <FaSignOutAlt className="w-5 h-5" />
              <span>Logout</span>
            </button>
          </div>
        </nav>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col overflow-hidden">
        {/* Top bar */}
        <header className="bg-white shadow-sm z-20 border-b border-gray-200">
          <div className="flex items-center justify-between p-4">
            <div className="flex items-center">
              <button
                className="md:hidden mr-4 text-gray-600 hover:text-gray-900"
                onClick={() => setIsSidebarOpen(!isSidebarOpen)}
              >
                <FaBars className="h-6 w-6" />
              </button>
              <h1 className="text-xl font-semibold text-gray-800">{getCurrentPageTitle()}</h1>
            </div>

            <div className="flex items-center space-x-4">
              {/* Search */}
              <div className="hidden md:flex items-center bg-gray-100 rounded-lg px-3 py-2">
                <FaSearch className="text-gray-500 mr-2" />
                <input 
                  type="text" 
                  placeholder="Search..." 
                  className="bg-transparent border-none focus:outline-none text-sm w-40"
                />
              </div>

              {/* Notifications */}
              <div className="relative">
                <button 
                  className="p-2 text-gray-600 hover:text-gray-900 hover:bg-gray-100 rounded-full relative"
                  onClick={() => setNotificationsOpen(!notificationsOpen)}
                >
                  <FaBell className="h-5 w-5" />
                  <span className="absolute top-0 right-0 h-2 w-2 rounded-full bg-red-500"></span>
                </button>

                {notificationsOpen && (
                  <div className="absolute right-0 mt-2 w-80 bg-white rounded-lg shadow-lg py-2 z-50 border border-gray-200">
                    <div className="px-4 py-2 border-b border-gray-200">
                      <h3 className="text-sm font-semibold text-gray-700">Notifications</h3>
                    </div>
                    <div className="max-h-96 overflow-y-auto">
                      {notifications.map(notification => (
                        <div key={notification.id} className={`px-4 py-3 hover:bg-gray-50 ${!notification.read ? 'bg-blue-50' : ''}`}>
                          <p className="text-sm text-gray-800">{notification.text}</p>
                          <p className="text-xs text-gray-500 mt-1">{notification.time}</p>
                        </div>
                      ))}
                    </div>
                    <div className="px-4 py-2 border-t border-gray-200">
                      <button className="text-sm text-navy hover:text-navy-dark font-medium">View all notifications</button>
                    </div>
                  </div>
                )}
              </div>

              {/* User Menu */}
              <div className="relative">
                <button 
                  className="flex items-center space-x-2 text-gray-700 hover:text-gray-900"
                  onClick={() => setUserMenuOpen(!userMenuOpen)}
                >
                  <FaUserCircle className="h-8 w-8 text-gray-600" />
                  <span className="hidden md:inline-block font-medium text-sm">Admin</span>
                  <FaChevronDown className="h-4 w-4 text-gray-500" />
                </button>

                {userMenuOpen && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-lg py-2 z-50 border border-gray-200">
                    <Link to="/admin/profile" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Your Profile</Link>
                    <Link to="/admin/settings" className="block px-4 py-2 text-sm text-gray-700 hover:bg-gray-100">Settings</Link>
                    <div className="border-t border-gray-200 my-1"></div>
                    <button onClick={handleLogout} className="block w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-gray-100">Logout</button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </header>

        {/* Content */}
        <main className="flex-1 overflow-x-hidden overflow-y-auto bg-gray-100">
          <div className="container mx-auto px-4 sm:px-6 py-8">
            <Routes>
              <Route index element={<Overview stats={stats} />} />
              <Route path="content/*" element={<ContentManagement />} />
              <Route path="courses/*" element={<CoursesManagement />} />
              <Route path="services/*" element={<ServicesManagement />} />
              <Route path="services-page/*" element={<ServicesPageManagement />} />
              <Route path="testimonials/*" element={<TestimonialsManagement />} />
              <Route path="hero/*" element={<HeroSectionManagement />} />
              <Route path="about/*" element={<AboutSectionManagement />} />
              <Route path="countries/*" element={<CountriesManagement />} />
              <Route path="about-page/*" element={<AboutPageManagement />} />
              <Route path="contact-page/*" element={<ContactPageManagement />} />
            </Routes>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
